using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for AlertRange class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnAlertRange
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void Hookget_IsHeroInRange()
        {
            if (!HookedList.Contains("get_IsHeroInRange"))
            {
                HookedList.Add("get_IsHeroInRange");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(AlertRange), "get_IsHeroInRange", true),
                    get_IsHeroInRange);
            }
        }

        internal static event Delegates.get_IsHeroInRange_BeforeArgs _beforeget_IsHeroInRange;
        internal static event Delegates.get_IsHeroInRange_AfterArgs _afterget_IsHeroInRange;

        private static bool get_IsHeroInRange(Func<AlertRange, bool> orig, AlertRange self)
        {
            Delegates.Params_get_IsHeroInRange @params = new()
            {
                self = self
            };
            if (_beforeget_IsHeroInRange != null)
            {
                foreach (Delegates.get_IsHeroInRange_BeforeArgs toInvoke in
                         _beforeget_IsHeroInRange.GetInvocationList())
                {
                    try
                    {
                        _beforeget_IsHeroInRange?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_IsHeroInRange != null)
            {
                foreach (Delegates.get_IsHeroInRange_AfterArgs toInvoke in _afterget_IsHeroInRange.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_IsHeroInRange.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

        internal static void HookAwake()
        {
            if (!HookedList.Contains("Awake"))
            {
                HookedList.Add("Awake");
                On.AlertRange.Awake += Awake;
            }
        }

        internal static event Delegates.Awake_BeforeArgs _beforeAwake;
        internal static event Delegates.Awake_AfterArgs _afterAwake;

        private static void Awake(On.AlertRange.orig_Awake orig, AlertRange self)
        {
            Delegates.Params_Awake @params = new()
            {
                self = self
            };
            if (_beforeAwake != null)
            {
                foreach (Delegates.Awake_BeforeArgs toInvoke in _beforeAwake.GetInvocationList())
                {
