        internal static event Delegates.Find_BeforeArgs _beforeFind;
        internal static event Delegates.Find_AfterArgs _afterFind;

        private static AlertRange Find(On.AlertRange.orig_Find orig, UnityEngine.GameObject root, string childName)
        {
            Delegates.Params_Find @params = new()
            {
                root = root, childName = childName
