    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_get_IsHeroInRange
        {
            public AlertRange self;
        }

        public delegate void get_IsHeroInRange_BeforeArgs(Params_get_IsHeroInRange args);

        public delegate bool get_IsHeroInRange_AfterArgs(Params_get_IsHeroInRange args, bool ret);

        public delegate bool get_IsHeroInRange_WithArgs(Func<AlertRange, bool> orig, AlertRange self);

        public sealed class Params_Awake
        {
            public AlertRange self;
        }

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<AlertRange> orig, AlertRange self);

        public sealed class Params_OnTriggerEnter2D
        {
            public AlertRange self;
            public UnityEngine.Collider2D other;
        }

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<AlertRange, UnityEngine.Collider2D> orig, AlertRange self,
            UnityEngine.Collider2D other);

        public sealed class Params_OnTriggerExit2D
        {
            public AlertRange self;
            public UnityEngine.Collider2D other;
        }

        public delegate void OnTriggerExit2D_BeforeArgs(Params_OnTriggerExit2D args);

        public delegate void OnTriggerExit2D_AfterArgs(Params_OnTriggerExit2D args);

        public delegate void OnTriggerExit2D_WithArgs(Action<AlertRange, UnityEngine.Collider2D> orig, AlertRange self,
            UnityEngine.Collider2D other);

        public sealed class Params_StillInColliders
        {
            public AlertRange self;
        }

        public delegate void StillInColliders_BeforeArgs(Params_StillInColliders args);

        public delegate bool StillInColliders_AfterArgs(Params_StillInColliders args, bool ret);

        public delegate bool StillInColliders_WithArgs(Func<AlertRange, bool> orig, AlertRange self);

        public sealed class Params_Find
        {
            public UnityEngine.GameObject root;
            public string childName;
        }

        public delegate void Find_BeforeArgs(Params_Find args);

        public delegate AlertRange Find_AfterArgs(Params_Find args, AlertRange ret);

        public delegate AlertRange Find_WithArgs(Func<UnityEngine.GameObject, string, AlertRange> orig,
            UnityEngine.GameObject root, string childName);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_IsHeroInRange_BeforeArgs get_IsHeroInRange
        {
            add
            {
                HookHandler._beforeget_IsHeroInRange += value;
                HookHandler.Hookget_IsHeroInRange();
            }
            remove => HookHandler._beforeget_IsHeroInRange -= value;
        }

        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.OnTriggerEnter2D_BeforeArgs OnTriggerEnter2D
        {
            add
            {
