        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<ArrayForEach> orig, ArrayForEach self);

        public delegate void StartFsm_AfterArgs(Params_StartFsm args);

        public delegate void StartFsm_BeforeArgs(Params_StartFsm args);

        public delegate void StartFsm_WithArgs(Action<ArrayForEach> orig, ArrayForEach self);

