using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for ArrayListContainsGameObject class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnArrayListContainsGameObject
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<ArrayListContainsGameObject> orig, ArrayListContainsGameObject self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<ArrayListContainsGameObject> orig, ArrayListContainsGameObject self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookDoContainsGo()
        {
            if (!HookedList.Contains("DoContainsGo"))
            {
                HookedList.Add("DoContainsGo");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "DoContainsGo"),
                    DoContainsGo);
            }
        }

        internal static event Delegates.DoContainsGo_BeforeArgs _beforeDoContainsGo;
        internal static event Delegates.DoContainsGo_AfterArgs _afterDoContainsGo;

        private static int DoContainsGo(Func<ArrayListContainsGameObject, int> orig, ArrayListContainsGameObject self)
        {
            Delegates.Params_DoContainsGo @params = new()
            {
                self = self
            };
            if (_beforeDoContainsGo != null)
                foreach (Delegates.DoContainsGo_BeforeArgs toInvoke in _beforeDoContainsGo.GetInvocationList())
                    try
                    {
                        _beforeDoContainsGo?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            var retVal = orig(self);
            if (_afterDoContainsGo != null)
                foreach (Delegates.DoContainsGo_AfterArgs toInvoke in _afterDoContainsGo.GetInvocationList())
                    try
                    {
                        retVal = _afterDoContainsGo.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            return retVal;
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate int DoContainsGo_AfterArgs(Params_DoContainsGo args, int ret);

        public delegate void DoContainsGo_BeforeArgs(Params_DoContainsGo args);

        public delegate int DoContainsGo_WithArgs(Func<ArrayListContainsGameObject, int> orig,
            ArrayListContainsGameObject self);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<ArrayListContainsGameObject> orig,
            ArrayListContainsGameObject self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<ArrayListContainsGameObject> orig, ArrayListContainsGameObject self);

        public sealed class Params_Reset
        {
            public ArrayListContainsGameObject self;
        }

        public sealed class Params_OnEnter
        {
            public ArrayListContainsGameObject self;
        }

        public sealed class Params_DoContainsGo
        {
            public ArrayListContainsGameObject self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoContainsGo_BeforeArgs DoContainsGo
        {
            add
            {
                HookHandler._beforeDoContainsGo += value;
                HookHandler.HookDoContainsGo();
            }
            remove => HookHandler._beforeDoContainsGo -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoContainsGo_AfterArgs DoContainsGo
        {
            add
            {
                HookHandler._afterDoContainsGo += value;
                HookHandler.HookDoContainsGo();
            }
            remove => HookHandler._afterDoContainsGo -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "Reset"), value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "OnEnter"), value);
        }

        public static event Delegates.DoContainsGo_WithArgs DoContainsGo
        {
            add => HookEndpointManager.Add<Delegates.DoContainsGo_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "DoContainsGo"), value);
            remove => HookEndpointManager.Remove<Delegates.DoContainsGo_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListContainsGameObject), "DoContainsGo"), value);
        }
    }
}