            _beforeDoLineCast?.Invoke(@params);
            self = @params.self;
            toGameObject = @params.toGameObject;
            var retVal = orig(self, toGameObject);
            if (_afterDoLineCast != null) retVal = _afterDoLineCast.Invoke(@params);
            return retVal;
