            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookDoGetNearestValue()
        {
            if (!HookedList.Contains("DoGetNearestValue"))
            {
                HookedList.Add("DoGetNearestValue");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListGetNearestFloatValue), "DoGetNearestValue"),
                    DoGetNearestValue);
            }
        }

        internal static event Delegates.DoGetNearestValue_BeforeArgs _beforeDoGetNearestValue;
        internal static event Delegates.DoGetNearestValue_NormalArgs _afterDoGetNearestValue;

        private static void DoGetNearestValue(Action<ArrayListGetNearestFloatValue> orig,
