using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for ArrayListGetVertexPositions class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnArrayListGetVertexPositions
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<ArrayListGetVertexPositions> orig, ArrayListGetVertexPositions self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<ArrayListGetVertexPositions> orig, ArrayListGetVertexPositions self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookgetVertexPositions()
        {
            if (!HookedList.Contains("getVertexPositions"))
            {
                HookedList.Add("getVertexPositions");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "getVertexPositions"),
                    getVertexPositions);
            }
        }

        internal static event Delegates.getVertexPositions_BeforeArgs _beforegetVertexPositions;
        internal static event Delegates.getVertexPositions_NormalArgs _aftergetVertexPositions;

        private static void getVertexPositions(Action<ArrayListGetVertexPositions> orig,
            ArrayListGetVertexPositions self)
        {
            Delegates.Params_getVertexPositions @params = new()
            {
                self = self
            };
            _beforegetVertexPositions?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_aftergetVertexPositions != null) _aftergetVertexPositions.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void getVertexPositions_BeforeArgs(Params_getVertexPositions args);

        public delegate void getVertexPositions_NormalArgs(Params_getVertexPositions args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public ArrayListGetVertexPositions self;
        }

        public sealed class Params_OnEnter
        {
            public ArrayListGetVertexPositions self;
        }

        public sealed class Params_getVertexPositions
        {
            public ArrayListGetVertexPositions self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.getVertexPositions_BeforeArgs getVertexPositions
        {
            add
            {
                HookHandler._beforegetVertexPositions += value;
                HookHandler.HookgetVertexPositions();
            }
            remove => HookHandler._beforegetVertexPositions -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.getVertexPositions_NormalArgs getVertexPositions
        {
            add
            {
                HookHandler._aftergetVertexPositions += value;
                HookHandler.HookgetVertexPositions();
            }
            remove => HookHandler._aftergetVertexPositions -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "OnEnter"), value);
        }

        public static event Delegates.getVertexPositions_NormalArgs getVertexPositions
        {
            add => HookEndpointManager.Add<Delegates.getVertexPositions_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "getVertexPositions"), value);
            remove => HookEndpointManager.Remove<Delegates.getVertexPositions_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListGetVertexPositions), "getVertexPositions"), value);
        }
    }
}