using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for ArrayListInsert class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnArrayListInsert
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<ArrayListInsert> orig, ArrayListInsert self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<ArrayListInsert> orig, ArrayListInsert self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookdoArrayListInsert()
        {
            if (!HookedList.Contains("doArrayListInsert"))
            {
                HookedList.Add("doArrayListInsert");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "doArrayListInsert"),
                    doArrayListInsert);
            }
        }

        internal static event Delegates.doArrayListInsert_BeforeArgs _beforedoArrayListInsert;
        internal static event Delegates.doArrayListInsert_AfterArgs _afterdoArrayListInsert;

        private static void doArrayListInsert(Action<ArrayListInsert> orig, ArrayListInsert self)
        {
            Delegates.Params_doArrayListInsert @params = new()
            {
                self = self
            };
            if (_beforedoArrayListInsert != null)
                foreach (Delegates.doArrayListInsert_BeforeArgs toInvoke in
                         _beforedoArrayListInsert.GetInvocationList())
                    try
                    {
                        _beforedoArrayListInsert?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterdoArrayListInsert != null)
                foreach (Delegates.doArrayListInsert_AfterArgs toInvoke in _afterdoArrayListInsert.GetInvocationList())
                    try
                    {
                        _afterdoArrayListInsert.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void doArrayListInsert_AfterArgs(Params_doArrayListInsert args);

        public delegate void doArrayListInsert_BeforeArgs(Params_doArrayListInsert args);

        public delegate void doArrayListInsert_WithArgs(Action<ArrayListInsert> orig, ArrayListInsert self);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<ArrayListInsert> orig, ArrayListInsert self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<ArrayListInsert> orig, ArrayListInsert self);

        public sealed class Params_Reset
        {
            public ArrayListInsert self;
        }

        public sealed class Params_OnEnter
        {
            public ArrayListInsert self;
        }

        public sealed class Params_doArrayListInsert
        {
            public ArrayListInsert self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.doArrayListInsert_BeforeArgs doArrayListInsert
        {
            add
            {
                HookHandler._beforedoArrayListInsert += value;
                HookHandler.HookdoArrayListInsert();
            }
            remove => HookHandler._beforedoArrayListInsert -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.doArrayListInsert_AfterArgs doArrayListInsert
        {
            add
            {
                HookHandler._afterdoArrayListInsert += value;
                HookHandler.HookdoArrayListInsert();
            }
            remove => HookHandler._afterdoArrayListInsert -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "Reset"), value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "OnEnter"), value);
        }

        public static event Delegates.doArrayListInsert_WithArgs doArrayListInsert
        {
            add => HookEndpointManager.Add<Delegates.doArrayListInsert_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "doArrayListInsert"), value);
            remove => HookEndpointManager.Remove<Delegates.doArrayListInsert_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListInsert), "doArrayListInsert"), value);
        }
    }
}