{
public sealed class Params_Reset
{
public HutongGames.PlayMaker.Actions.ArrayListResetValues self;
}
public delegate void Reset_BeforeArgs(Params_Reset args);
public delegate void Reset_AfterArgs(Params_Reset args);
public delegate void Reset_WithArgs (Action<HutongGames.PlayMaker.Actions.ArrayListResetValues> orig, HutongGames.PlayMaker.Actions.ArrayListResetValues self);
public sealed class Params_OnEnter
{
public HutongGames.PlayMaker.Actions.ArrayListResetValues self;
