using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for ArrayListTakeSnapShot class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnArrayListTakeSnapShot
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<ArrayListTakeSnapShot> orig, ArrayListTakeSnapShot self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<ArrayListTakeSnapShot> orig, ArrayListTakeSnapShot self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoArrayListTakeSnapShot()
        {
            if (!HookedList.Contains("DoArrayListTakeSnapShot"))
            {
                HookedList.Add("DoArrayListTakeSnapShot");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "DoArrayListTakeSnapShot"),
                    DoArrayListTakeSnapShot);
            }
        }

        internal static event Delegates.DoArrayListTakeSnapShot_BeforeArgs _beforeDoArrayListTakeSnapShot;
        internal static event Delegates.DoArrayListTakeSnapShot_NormalArgs _afterDoArrayListTakeSnapShot;

        private static void DoArrayListTakeSnapShot(Action<ArrayListTakeSnapShot> orig, ArrayListTakeSnapShot self)
        {
            Delegates.Params_DoArrayListTakeSnapShot @params = new()
            {
                self = self
            };
            _beforeDoArrayListTakeSnapShot?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoArrayListTakeSnapShot != null) _afterDoArrayListTakeSnapShot.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoArrayListTakeSnapShot_BeforeArgs(Params_DoArrayListTakeSnapShot args);

        public delegate void DoArrayListTakeSnapShot_NormalArgs(Params_DoArrayListTakeSnapShot args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public ArrayListTakeSnapShot self;
        }

        public sealed class Params_OnEnter
        {
            public ArrayListTakeSnapShot self;
        }

        public sealed class Params_DoArrayListTakeSnapShot
        {
            public ArrayListTakeSnapShot self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoArrayListTakeSnapShot_BeforeArgs DoArrayListTakeSnapShot
        {
            add
            {
                HookHandler._beforeDoArrayListTakeSnapShot += value;
                HookHandler.HookDoArrayListTakeSnapShot();
            }
            remove => HookHandler._beforeDoArrayListTakeSnapShot -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoArrayListTakeSnapShot_NormalArgs DoArrayListTakeSnapShot
        {
            add
            {
                HookHandler._afterDoArrayListTakeSnapShot += value;
                HookHandler.HookDoArrayListTakeSnapShot();
            }
            remove => HookHandler._afterDoArrayListTakeSnapShot -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "OnEnter"), value);
        }

        public static event Delegates.DoArrayListTakeSnapShot_NormalArgs DoArrayListTakeSnapShot
        {
            add => HookEndpointManager.Add<Delegates.DoArrayListTakeSnapShot_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "DoArrayListTakeSnapShot"), value);
            remove => HookEndpointManager.Remove<Delegates.DoArrayListTakeSnapShot_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ArrayListTakeSnapShot), "DoArrayListTakeSnapShot"), value);
        }
    }
}