using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for AtmosCue class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnAtmosCue
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void Hookget_Snapshot()
        {
            if (!HookedList.Contains("get_Snapshot"))
            {
                HookedList.Add("get_Snapshot");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(AtmosCue), "get_Snapshot", true), get_Snapshot);
            }
        }

        internal static event Delegates.get_Snapshot_BeforeArgs _beforeget_Snapshot;
        internal static event Delegates.get_Snapshot_AfterArgs _afterget_Snapshot;

        private static UnityEngine.Audio.AudioMixerSnapshot get_Snapshot(
            Func<AtmosCue, UnityEngine.Audio.AudioMixerSnapshot> orig, AtmosCue self)
        {
            Delegates.Params_get_Snapshot @params = new()
            {
                self = self
            };
            if (_beforeget_Snapshot != null)
            {
                foreach (Delegates.get_Snapshot_BeforeArgs toInvoke in _beforeget_Snapshot.GetInvocationList())
                {
                    try
                    {
                        _beforeget_Snapshot?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_Snapshot != null)
            {
                foreach (Delegates.get_Snapshot_AfterArgs toInvoke in _afterget_Snapshot.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_Snapshot.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

        internal static void HookIsChannelEnabled()
        {
            if (!HookedList.Contains("IsChannelEnabled"))
            {
                HookedList.Add("IsChannelEnabled");
                On.AtmosCue.IsChannelEnabled += IsChannelEnabled;
            }
        }

        internal static event Delegates.IsChannelEnabled_BeforeArgs _beforeIsChannelEnabled;
        internal static event Delegates.IsChannelEnabled_AfterArgs _afterIsChannelEnabled;

        private static bool IsChannelEnabled(On.AtmosCue.orig_IsChannelEnabled orig, AtmosCue self,
            AtmosChannels channel)
        {
            Delegates.Params_IsChannelEnabled @params = new()
            {
                self = self, channel = channel
            };
            if (_beforeIsChannelEnabled != null)
            {
                foreach (Delegates.IsChannelEnabled_BeforeArgs toInvoke in _beforeIsChannelEnabled.GetInvocationList())
                {
                    try
                    {
                        _beforeIsChannelEnabled?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            channel = @params.channel;
            var retVal = orig(self, channel);
            if (_afterIsChannelEnabled != null)
            {
                foreach (Delegates.IsChannelEnabled_AfterArgs toInvoke in _afterIsChannelEnabled.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterIsChannelEnabled.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_get_Snapshot
        {
            public AtmosCue self;
        }

        public delegate void get_Snapshot_BeforeArgs(Params_get_Snapshot args);

        public delegate UnityEngine.Audio.AudioMixerSnapshot get_Snapshot_AfterArgs(Params_get_Snapshot args,
            UnityEngine.Audio.AudioMixerSnapshot ret);

        public delegate UnityEngine.Audio.AudioMixerSnapshot get_Snapshot_WithArgs(
            Func<AtmosCue, UnityEngine.Audio.AudioMixerSnapshot> orig, AtmosCue self);

        public sealed class Params_IsChannelEnabled
        {
            public AtmosCue self;
            public AtmosChannels channel;
        }

        public delegate void IsChannelEnabled_BeforeArgs(Params_IsChannelEnabled args);

        public delegate bool IsChannelEnabled_AfterArgs(Params_IsChannelEnabled args, bool ret);

        public delegate bool IsChannelEnabled_WithArgs(Func<AtmosCue, AtmosChannels, bool> orig, AtmosCue self,
            AtmosChannels channel);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_Snapshot_BeforeArgs get_Snapshot
        {
            add
            {
                HookHandler._beforeget_Snapshot += value;
                HookHandler.Hookget_Snapshot();
            }
            remove => HookHandler._beforeget_Snapshot -= value;
        }

        public static event Delegates.IsChannelEnabled_BeforeArgs IsChannelEnabled
        {
            add
            {
                HookHandler._beforeIsChannelEnabled += value;
                HookHandler.HookIsChannelEnabled();
            }
            remove => HookHandler._beforeIsChannelEnabled -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.get_Snapshot_AfterArgs get_Snapshot
        {
            add
            {
                HookHandler._afterget_Snapshot += value;
                HookHandler.Hookget_Snapshot();
            }
            remove => HookHandler._afterget_Snapshot -= value;
        }

        public static event Delegates.IsChannelEnabled_AfterArgs IsChannelEnabled
        {
            add
            {
                HookHandler._afterIsChannelEnabled += value;
                HookHandler.HookIsChannelEnabled();
            }
            remove => HookHandler._afterIsChannelEnabled -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_Snapshot_WithArgs get_Snapshot
        {
            add => HookEndpointManager.Add<Delegates.get_Snapshot_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(AtmosCue), "get_Snapshot", true), value);
            remove => HookEndpointManager.Remove<Delegates.get_Snapshot_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(AtmosCue), "get_Snapshot", true), value);
        }

        public static event On.AtmosCue.hook_IsChannelEnabled IsChannelEnabled
        {
            add => On.AtmosCue.IsChannelEnabled += value;
            remove => On.AtmosCue.IsChannelEnabled -= value;
        }
    }
}