                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            return orig(self);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate IEnumerator Animate_AfterArgs(Params_Animate args, IEnumerator ret);

        public delegate void Animate_BeforeArgs(Params_Animate args);

        public delegate IEnumerator Animate_WithArgs(Func<BasicSpriteAnimator, IEnumerator> orig,
            BasicSpriteAnimator self);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<BasicSpriteAnimator> orig, BasicSpriteAnimator self);

        public delegate float get_Length_AfterArgs(Params_get_Length args, float ret);

        public delegate void get_Length_BeforeArgs(Params_get_Length args);

        public delegate float get_Length_WithArgs(Func<BasicSpriteAnimator, float> orig, BasicSpriteAnimator self);

        public delegate void OnDisable_AfterArgs(Params_OnDisable args);

        public delegate void OnDisable_BeforeArgs(Params_OnDisable args);

        public delegate void OnDisable_WithArgs(Action<BasicSpriteAnimator> orig, BasicSpriteAnimator self);

        public delegate void OnEnable_AfterArgs(Params_OnEnable args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_WithArgs(Action<BasicSpriteAnimator> orig, BasicSpriteAnimator self);

        public sealed class Params_get_Length
        {
            public BasicSpriteAnimator self;
        }

        public sealed class Params_Awake
        {
            public BasicSpriteAnimator self;
        }

        public sealed class Params_OnEnable
        {
            public BasicSpriteAnimator self;
        }

        public sealed class Params_OnDisable
        {
            public BasicSpriteAnimator self;
        }

        public sealed class Params_Animate
        {
            public BasicSpriteAnimator self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_Length_BeforeArgs get_Length
        {
            add
            {
                HookHandler._beforeget_Length += value;
                HookHandler.Hookget_Length();
            }
            remove => HookHandler._beforeget_Length -= value;
        }

        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.OnDisable_BeforeArgs OnDisable
        {
            add
            {
                HookHandler._beforeOnDisable += value;
                HookHandler.HookOnDisable();
            }
            remove => HookHandler._beforeOnDisable -= value;
        }

        public static event Delegates.Animate_BeforeArgs Animate
        {
            add
            {
                HookHandler._beforeAnimate += value;
                HookHandler.HookAnimate();
            }
            remove => HookHandler._beforeAnimate -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
