        internal static void HookDoAnyTrue()
        {
            if (!HookedList.Contains("DoAnyTrue"))
            {
                HookedList.Add("DoAnyTrue");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(BoolAnyTrue), "DoAnyTrue"), DoAnyTrue);
            }
        }

        internal static event Delegates.DoAnyTrue_BeforeArgs _beforeDoAnyTrue;
        internal static event Delegates.DoAnyTrue_AfterArgs _afterDoAnyTrue;

        private static void DoAnyTrue(Action<BoolAnyTrue> orig, BoolAnyTrue self)
        {
            Delegates.Params_DoAnyTrue @params = new()
            {
                self = self
            };
            if (_beforeDoAnyTrue != null)
                foreach (Delegates.DoAnyTrue_BeforeArgs toInvoke in _beforeDoAnyTrue.GetInvocationList())
