    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<BossChallengeUI> orig, BossChallengeUI self);

        public delegate void Hide_AfterArgs(Params_Hide args);

        public delegate void Hide_BeforeArgs(Params_Hide args);

        public delegate void Hide_bool_AfterArgs(Params_Hide_bool args);

        public delegate void Hide_bool_BeforeArgs(Params_Hide_bool args);

        public delegate void Hide_bool_WithArgs(Action<BossChallengeUI, bool> orig, BossChallengeUI self, bool doAnim);

        public delegate void Hide_WithArgs(Action<BossChallengeUI> orig, BossChallengeUI self);

        public delegate IEnumerator HideAnim_AfterArgs(Params_HideAnim args, IEnumerator ret);

        public delegate void HideAnim_BeforeArgs(Params_HideAnim args);

        public delegate IEnumerator HideAnim_WithArgs(Func<BossChallengeUI, IEnumerator> orig, BossChallengeUI self);

        public delegate void LoadBoss_int_AfterArgs(Params_LoadBoss_int args);

        public delegate void LoadBoss_int_BeforeArgs(Params_LoadBoss_int args);

        public delegate void LoadBoss_int_bool_AfterArgs(Params_LoadBoss_int_bool args);

        public delegate void LoadBoss_int_bool_BeforeArgs(Params_LoadBoss_int_bool args);

        public delegate void LoadBoss_int_bool_WithArgs(Action<BossChallengeUI, int, bool> orig, BossChallengeUI self,
            int level, bool doHideAnim);

        public delegate void LoadBoss_int_WithArgs(Action<BossChallengeUI, int> orig, BossChallengeUI self, int level);

        public delegate void RecordLastSelected_AfterArgs(Params_RecordLastSelected args);

        public delegate void RecordLastSelected_BeforeArgs(Params_RecordLastSelected args);

        public delegate void RecordLastSelected_WithArgs(Action<BossChallengeUI, int> orig, BossChallengeUI self,
            int index);

        public delegate IEnumerator SetFirstSelected_AfterArgs(Params_SetFirstSelected args, IEnumerator ret);

        public delegate void SetFirstSelected_BeforeArgs(Params_SetFirstSelected args);

        public delegate IEnumerator SetFirstSelected_WithArgs(Func<BossChallengeUI, IEnumerator> orig,
            BossChallengeUI self);

        public delegate void Setup_AfterArgs(Params_Setup args);

        public delegate void Setup_BeforeArgs(Params_Setup args);

        public delegate void Setup_WithArgs(Action<BossChallengeUI, BossStatue, string, string, string, string> orig,
            BossChallengeUI self, BossStatue bossStatue, string bossNameSheet, string bossNameKey,
            string descriptionSheet, string descriptionKey);

        public delegate IEnumerator ShowUnlockEffect_AfterArgs(Params_ShowUnlockEffect args, IEnumerator ret);

        public delegate void ShowUnlockEffect_BeforeArgs(Params_ShowUnlockEffect args);

        public delegate IEnumerator ShowUnlockEffect_WithArgs(Func<BossChallengeUI, IEnumerator> orig,
            BossChallengeUI self);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<BossChallengeUI> orig, BossChallengeUI self);

        public sealed class Params_Awake
        {
            public BossChallengeUI self;
        }

        public sealed class Params_Start
        {
            public BossChallengeUI self;
        }

        public sealed class Params_Setup
        {
            public string bossNameKey;
            public string bossNameSheet;
            public BossStatue bossStatue;
            public string descriptionKey;
            public string descriptionSheet;
            public BossChallengeUI self;
        }

        public sealed class Params_ShowUnlockEffect
        {
            public BossChallengeUI self;
        }

        public sealed class Params_SetFirstSelected
        {
            public BossChallengeUI self;
        }

        public sealed class Params_Hide
        {
            public BossChallengeUI self;
        }

        public sealed class Params_Hide_bool
        {
            public bool doAnim;
            public BossChallengeUI self;
        }

        public sealed class Params_HideAnim
        {
            public BossChallengeUI self;
        }

        public sealed class Params_LoadBoss_int
        {
            public int level;
            public BossChallengeUI self;
        }

        public sealed class Params_LoadBoss_int_bool
        {
            public bool doHideAnim;
            public int level;
            public BossChallengeUI self;
        }

        public sealed class Params_RecordLastSelected
        {
            public int index;
            public BossChallengeUI self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
