        internal static void Hookget_Selected()
        {
            if (!HookedList.Contains("get_Selected"))
            {
                HookedList.Add("get_Selected");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(BossDoorChallengeUIBindingButton), "get_Selected", true),
                    get_Selected);
            }
        }

        internal static event Delegates.get_Selected_BeforeArgs _beforeget_Selected;
        internal static event Delegates.get_Selected_AfterArgs _afterget_Selected;

        private static bool get_Selected(Func<BossDoorChallengeUIBindingButton, bool> orig,
            BossDoorChallengeUIBindingButton self)
        {
            Delegates.Params_get_Selected @params = new()
            {
                self = self
            };
            if (_beforeget_Selected != null)
            {
                foreach (Delegates.get_Selected_BeforeArgs toInvoke in _beforeget_Selected.GetInvocationList())
                {
                    try
                    {
                        _beforeget_Selected?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_Selected != null)
            {
                foreach (Delegates.get_Selected_AfterArgs toInvoke in _afterget_Selected.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_Selected.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

