        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<BossStatueDreamToggle, UnityEngine.Collider2D> orig,
            BossStatueDreamToggle self, UnityEngine.Collider2D collision);

        public sealed class Params_Fade
        {
            public BossStatueDreamToggle self;
            public bool usingDreamVersion;
        }

        public delegate void Fade_BeforeArgs(Params_Fade args);

        public delegate System.Collections.IEnumerator Fade_AfterArgs(Params_Fade args,
            System.Collections.IEnumerator ret);

        public delegate System.Collections.IEnumerator Fade_WithArgs(
            Func<BossStatueDreamToggle, bool, System.Collections.IEnumerator> orig, BossStatueDreamToggle self,
            bool usingDreamVersion);

