/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_Awake
{
public BossStatueFlashEffect self;
}
public delegate void Awake_BeforeArgs(Params_Awake args);
public delegate void Awake_AfterArgs(Params_Awake args);
public delegate void Awake_WithArgs (Action<BossStatueFlashEffect> orig, BossStatueFlashEffect self);
public sealed class Params_Start
{
public BossStatueFlashEffect self;
}
public delegate void Start_BeforeArgs(Params_Start args);
public delegate void Start_AfterArgs(Params_Start args);
public delegate void Start_WithArgs (Action<BossStatueFlashEffect> orig, BossStatueFlashEffect self);
public sealed class Params_FlashRoutine
{
public BossStatueFlashEffect self;
}
public delegate void FlashRoutine_BeforeArgs(Params_FlashRoutine args);
public delegate System.Collections.IEnumerator FlashRoutine_AfterArgs(Params_FlashRoutine args, System.Collections.IEnumerator ret);
public delegate System.Collections.IEnumerator FlashRoutine_WithArgs (Func<BossStatueFlashEffect, System.Collections.IEnumerator> orig, BossStatueFlashEffect self);
public sealed class Params_FlashApex
{
public BossStatueFlashEffect self;
}
public delegate void FlashApex_BeforeArgs(Params_FlashApex args);
public delegate void FlashApex_AfterArgs(Params_FlashApex args);
public delegate void FlashApex_WithArgs (Action<BossStatueFlashEffect> orig, BossStatueFlashEffect self);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.Awake_BeforeArgs Awake
{
add
{

HookHandler._beforeAwake += value;
HookHandler.HookAwake();
}
remove => HookHandler._beforeAwake -= value;
}
public static event Delegates.Start_BeforeArgs Start
{
add
{

