namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for BridgeLever class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnBridgeLever
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookAwake()
        {
            if (!HookedList.Contains("Awake"))
            {
                HookedList.Add("Awake");
                On.BridgeLever.Awake += Awake;
            }
        }

        internal static event Delegates.Awake_BeforeArgs _beforeAwake;
        internal static event Delegates.Awake_NormalArgs _afterAwake;

        private static void Awake(On.BridgeLever.orig_Awake orig, BridgeLever self)
        {
            Delegates.Params_Awake @params = new()
            {
                self = self
            };
            _beforeAwake?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterAwake != null) _afterAwake.Invoke(@params);
        }

        internal static void HookStart()
        {
            if (!HookedList.Contains("Start"))
            {
                HookedList.Add("Start");
                On.BridgeLever.Start += Start;
            }
        }

        internal static event Delegates.Start_BeforeArgs _beforeStart;
        internal static event Delegates.Start_NormalArgs _afterStart;

        private static void Start(On.BridgeLever.orig_Start orig, BridgeLever self)
        {
            Delegates.Params_Start @params = new()
            {
                self = self
            };
            _beforeStart?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterStart != null) _afterStart.Invoke(@params);
        }

        internal static void HookOnTriggerEnter2D()
        {
            if (!HookedList.Contains("OnTriggerEnter2D"))
            {
                HookedList.Add("OnTriggerEnter2D");
                On.BridgeLever.OnTriggerEnter2D += OnTriggerEnter2D;
            }
        }

        internal static event Delegates.OnTriggerEnter2D_BeforeArgs _beforeOnTriggerEnter2D;
        internal static event Delegates.OnTriggerEnter2D_NormalArgs _afterOnTriggerEnter2D;

        private static void OnTriggerEnter2D(On.BridgeLever.orig_OnTriggerEnter2D orig, BridgeLever self,
            Collider2D collision)
        {
            Delegates.Params_OnTriggerEnter2D @params = new()
            {
                self = self, collision = collision
            };
            _beforeOnTriggerEnter2D?.Invoke(@params);
            self = @params.self;
            collision = @params.collision;
            orig(self, collision);
            if (_afterOnTriggerEnter2D != null) _afterOnTriggerEnter2D.Invoke(@params);
        }

        internal static void HookOpenBridge()
        {
            if (!HookedList.Contains("OpenBridge"))
            {
                HookedList.Add("OpenBridge");
                On.BridgeLever.OpenBridge += OpenBridge;
            }
        }

        internal static event Delegates.OpenBridge_BeforeArgs _beforeOpenBridge;

        private static IEnumerator OpenBridge(On.BridgeLever.orig_OpenBridge orig, BridgeLever self)
        {
            Delegates.Params_OpenBridge @params = new()
            {
                self = self
            };
            _beforeOpenBridge?.Invoke(@params);
            self = @params.self;
            return orig(self);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_NormalArgs(Params_Awake args);

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_NormalArgs(Params_OnTriggerEnter2D args);

        public delegate void OpenBridge_BeforeArgs(Params_OpenBridge args);

        public delegate IEnumerator OpenBridge_NormalArgs(Params_OpenBridge args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_NormalArgs(Params_Start args);

        public sealed class Params_Awake
        {
            public BridgeLever self;
        }

        public sealed class Params_Start
        {
            public BridgeLever self;
        }

        public sealed class Params_OnTriggerEnter2D
        {
            public Collider2D collision;
            public BridgeLever self;
        }

        public sealed class Params_OpenBridge
        {
            public BridgeLever self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.OnTriggerEnter2D_BeforeArgs OnTriggerEnter2D
        {
            add
            {
                HookHandler._beforeOnTriggerEnter2D += value;
                HookHandler.HookOnTriggerEnter2D();
            }
            remove => HookHandler._beforeOnTriggerEnter2D -= value;
        }

        public static event Delegates.OpenBridge_BeforeArgs OpenBridge
        {
            add
            {
                HookHandler._beforeOpenBridge += value;
                HookHandler.HookOpenBridge();
            }
            remove => HookHandler._beforeOpenBridge -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Awake_NormalArgs Awake
        {
            add
            {
                HookHandler._afterAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._afterAwake -= value;
        }

        public static event Delegates.Start_NormalArgs Start
        {
            add
            {
                HookHandler._afterStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._afterStart -= value;
        }

        public static event Delegates.OnTriggerEnter2D_NormalArgs OnTriggerEnter2D
        {
            add
            {
                HookHandler._afterOnTriggerEnter2D += value;
                HookHandler.HookOnTriggerEnter2D();
            }
            remove => HookHandler._afterOnTriggerEnter2D -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.BridgeLever.hook_Awake Awake
        {
            add => On.BridgeLever.Awake += value;
            remove => On.BridgeLever.Awake -= value;
        }

        public static event On.BridgeLever.hook_Start Start
        {
            add => On.BridgeLever.Start += value;
            remove => On.BridgeLever.Start -= value;
        }

        public static event On.BridgeLever.hook_OnTriggerEnter2D OnTriggerEnter2D
        {
            add => On.BridgeLever.OnTriggerEnter2D += value;
            remove => On.BridgeLever.OnTriggerEnter2D -= value;
        }

        public static event On.BridgeLever.hook_OpenBridge OpenBridge
        {
            add => On.BridgeLever.OpenBridge += value;
            remove => On.BridgeLever.OpenBridge -= value;
        }
    }
}