namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for Bullet class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnBullet
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookOnEnable()
        {
            if (!HookedList.Contains("OnEnable"))
            {
                HookedList.Add("OnEnable");
                On.Bullet.OnEnable += OnEnable;
            }
        }

        internal static event Delegates.OnEnable_BeforeArgs _beforeOnEnable;
        internal static event Delegates.OnEnable_NormalArgs _afterOnEnable;

        private static void OnEnable(On.Bullet.orig_OnEnable orig, Bullet self)
        {
            Delegates.Params_OnEnable @params = new()
            {
                self = self
            };
            _beforeOnEnable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnable != null) _afterOnEnable.Invoke(@params);
        }

        internal static void HookOnDisable()
        {
            if (!HookedList.Contains("OnDisable"))
            {
                HookedList.Add("OnDisable");
                On.Bullet.OnDisable += OnDisable;
            }
        }

        internal static event Delegates.OnDisable_BeforeArgs _beforeOnDisable;
        internal static event Delegates.OnDisable_NormalArgs _afterOnDisable;

        private static void OnDisable(On.Bullet.orig_OnDisable orig, Bullet self)
        {
            Delegates.Params_OnDisable @params = new()
            {
                self = self
            };
            _beforeOnDisable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnDisable != null) _afterOnDisable.Invoke(@params);
        }

        internal static void HookShoot()
        {
            if (!HookedList.Contains("Shoot"))
            {
                HookedList.Add("Shoot");
                On.Bullet.Shoot += Shoot;
            }
        }

        internal static event Delegates.Shoot_BeforeArgs _beforeShoot;

        private static IEnumerator Shoot(On.Bullet.orig_Shoot orig, Bullet self)
        {
            Delegates.Params_Shoot @params = new()
            {
                self = self
            };
            _beforeShoot?.Invoke(@params);
            self = @params.self;
            return orig(self);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void OnDisable_BeforeArgs(Params_OnDisable args);

        public delegate void OnDisable_NormalArgs(Params_OnDisable args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_NormalArgs(Params_OnEnable args);

        public delegate void Shoot_BeforeArgs(Params_Shoot args);

        public delegate IEnumerator Shoot_NormalArgs(Params_Shoot args);

        public sealed class Params_OnEnable
        {
            public Bullet self;
        }

        public sealed class Params_OnDisable
        {
            public Bullet self;
        }

        public sealed class Params_Shoot
        {
            public Bullet self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.OnDisable_BeforeArgs OnDisable
        {
            add
            {
                HookHandler._beforeOnDisable += value;
                HookHandler.HookOnDisable();
            }
            remove => HookHandler._beforeOnDisable -= value;
        }

        public static event Delegates.Shoot_BeforeArgs Shoot
        {
            add
            {
                HookHandler._beforeShoot += value;
                HookHandler.HookShoot();
            }
            remove => HookHandler._beforeShoot -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.OnEnable_NormalArgs OnEnable
        {
            add
            {
                HookHandler._afterOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._afterOnEnable -= value;
        }

        public static event Delegates.OnDisable_NormalArgs OnDisable
        {
            add
            {
                HookHandler._afterOnDisable += value;
                HookHandler.HookOnDisable();
            }
            remove => HookHandler._afterOnDisable -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.Bullet.hook_OnEnable OnEnable
        {
            add => On.Bullet.OnEnable += value;
            remove => On.Bullet.OnEnable -= value;
        }

        public static event On.Bullet.hook_OnDisable OnDisable
        {
            add => On.Bullet.OnDisable += value;
            remove => On.Bullet.OnDisable -= value;
        }

        public static event On.Bullet.hook_Shoot Shoot
        {
            add => On.Bullet.Shoot += value;
            remove => On.Bullet.Shoot -= value;
        }
    }
}