    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void EnterLockZone_AfterArgs(Params_EnterLockZone args);

        public delegate void EnterLockZone_BeforeArgs(Params_EnterLockZone args);

        public delegate void EnterLockZone_WithArgs(Action<CameraTarget, float, float, float, float> orig,
            CameraTarget self, float xLockMin_var, float xLockMax_var, float yLockMin_var, float yLockMax_var);

        public delegate void EnterLockZoneInstant_AfterArgs(Params_EnterLockZoneInstant args);

        public delegate void EnterLockZoneInstant_BeforeArgs(Params_EnterLockZoneInstant args);

        public delegate void EnterLockZoneInstant_WithArgs(Action<CameraTarget, float, float, float, float> orig,
            CameraTarget self, float xLockMin_var, float xLockMax_var, float yLockMin_var, float yLockMax_var);

        public delegate void ExitLockZone_AfterArgs(Params_ExitLockZone args);

        public delegate void ExitLockZone_BeforeArgs(Params_ExitLockZone args);

        public delegate void ExitLockZone_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public delegate void FreezeInPlace_AfterArgs(Params_FreezeInPlace args);

        public delegate void FreezeInPlace_BeforeArgs(Params_FreezeInPlace args);

        public delegate void FreezeInPlace_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public delegate void GameInit_AfterArgs(Params_GameInit args);

        public delegate void GameInit_BeforeArgs(Params_GameInit args);

        public delegate void GameInit_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public delegate void PositionToStart_AfterArgs(Params_PositionToStart args);

        public delegate void PositionToStart_BeforeArgs(Params_PositionToStart args);

        public delegate void PositionToStart_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public delegate void SceneInit_AfterArgs(Params_SceneInit args);

        public delegate void SceneInit_BeforeArgs(Params_SceneInit args);

        public delegate void SceneInit_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public delegate void SetDampTime_AfterArgs(Params_SetDampTime args);

        public delegate void SetDampTime_BeforeArgs(Params_SetDampTime args);

        public delegate void SetDampTime_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public delegate void SetQuake_AfterArgs(Params_SetQuake args);

        public delegate void SetQuake_BeforeArgs(Params_SetQuake args);

        public delegate void SetQuake_WithArgs(Action<CameraTarget, bool> orig, CameraTarget self, bool quake);

        public delegate void SetSuperDash_AfterArgs(Params_SetSuperDash args);

        public delegate void SetSuperDash_BeforeArgs(Params_SetSuperDash args);

        public delegate void SetSuperDash_WithArgs(Action<CameraTarget, bool> orig, CameraTarget self, bool active);

        public delegate void Update_AfterArgs(Params_Update args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_WithArgs(Action<CameraTarget> orig, CameraTarget self);

        public sealed class Params_GameInit
        {
            public CameraTarget self;
        }

        public sealed class Params_SceneInit
        {
            public CameraTarget self;
        }

        public sealed class Params_Update
        {
            public CameraTarget self;
        }

        public sealed class Params_EnterLockZone
        {
            public CameraTarget self;
            public float xLockMax_var;
            public float xLockMin_var;
            public float yLockMax_var;
            public float yLockMin_var;
        }

        public sealed class Params_EnterLockZoneInstant
        {
            public CameraTarget self;
            public float xLockMax_var;
            public float xLockMin_var;
            public float yLockMax_var;
            public float yLockMin_var;
        }

        public sealed class Params_ExitLockZone
        {
            public CameraTarget self;
        }

        public sealed class Params_SetDampTime
        {
            public CameraTarget self;
        }

        public sealed class Params_SetSuperDash
        {
            public bool active;
            public CameraTarget self;
        }

        public sealed class Params_SetQuake
        {
            public bool quake;
            public CameraTarget self;
        }

        public sealed class Params_FreezeInPlace
        {
            public CameraTarget self;
        }

        public sealed class Params_PositionToStart
        {
            public CameraTarget self;
        }
    }

