        private static List<string> HookedList = new List<string>();

        internal static void Hookget_CurrentSequence()
        {
            if (!HookedList.Contains("get_CurrentSequence"))
            {
                HookedList.Add("get_CurrentSequence");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(ChainSequence), "get_CurrentSequence", true),
                    get_CurrentSequence);
            }
        }

        internal static event Delegates.get_CurrentSequence_BeforeArgs _beforeget_CurrentSequence;
        internal static event Delegates.get_CurrentSequence_AfterArgs _afterget_CurrentSequence;

        private static SkippableSequence get_CurrentSequence(Func<ChainSequence, SkippableSequence> orig,
            ChainSequence self)
        {
            Delegates.Params_get_CurrentSequence @params = new()
            {
                self = self
            };
            if (_beforeget_CurrentSequence != null)
            {
                foreach (Delegates.get_CurrentSequence_BeforeArgs toInvoke in _beforeget_CurrentSequence
                             .GetInvocationList())
                {
                    try
                    {
                        _beforeget_CurrentSequence?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_CurrentSequence != null)
            {
                foreach (Delegates.get_CurrentSequence_AfterArgs toInvoke in
                         _afterget_CurrentSequence.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_CurrentSequence.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

