    {
        public static event Delegates.get_IsCheatsEnabled_NormalArgs get_IsCheatsEnabled
        {
            add => HookEndpointManager.Add<Delegates.get_IsCheatsEnabled_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(CheatManager), "get_IsCheatsEnabled", false), value);
            remove => HookEndpointManager.Remove<Delegates.get_IsCheatsEnabled_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(CheatManager), "get_IsCheatsEnabled", false), value);
        }

        public static event On.CheatManager.hook_Init Init
