using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for CheckCollisionSideEnter class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnCheckCollisionSideEnter
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CheckCollisionSideEnter), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<CheckCollisionSideEnter> orig, CheckCollisionSideEnter self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CheckCollisionSideEnter), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<CheckCollisionSideEnter> orig, CheckCollisionSideEnter self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookOnExit()
        {
            if (!HookedList.Contains("OnExit"))
            {
                HookedList.Add("OnExit");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CheckCollisionSideEnter), "OnExit"), OnExit);
            }
        }

        internal static event Delegates.OnExit_BeforeArgs _beforeOnExit;
        internal static event Delegates.OnExit_NormalArgs _afterOnExit;

        private static void OnExit(Action<CheckCollisionSideEnter> orig, CheckCollisionSideEnter self)
        {
            Delegates.Params_OnExit @params = new()
            {
                self = self
            };
            _beforeOnExit?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnExit != null) _afterOnExit.Invoke(@params);
        }

        internal static void HookOnUpdate()
        {
            if (!HookedList.Contains("OnUpdate"))
            {
                HookedList.Add("OnUpdate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CheckCollisionSideEnter), "OnUpdate"), OnUpdate);
            }
        }

        internal static event Delegates.OnUpdate_BeforeArgs _beforeOnUpdate;
        internal static event Delegates.OnUpdate_NormalArgs _afterOnUpdate;

        private static void OnUpdate(Action<CheckCollisionSideEnter> orig, CheckCollisionSideEnter self)
        {
            Delegates.Params_OnUpdate @params = new()
            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookDoCollisionEnter2D()
        {
            if (!HookedList.Contains("DoCollisionEnter2D"))
            {
                HookedList.Add("DoCollisionEnter2D");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CheckCollisionSideEnter), "DoCollisionEnter2D"),
                    DoCollisionEnter2D);
            }
        }

        internal static event Delegates.DoCollisionEnter2D_BeforeArgs _beforeDoCollisionEnter2D;
        internal static event Delegates.DoCollisionEnter2D_NormalArgs _afterDoCollisionEnter2D;

        private static void DoCollisionEnter2D(Action<CheckCollisionSideEnter, Collision2D> orig,
            CheckCollisionSideEnter self, Collision2D collision)
        {
            Delegates.Params_DoCollisionEnter2D @params = new()
            {
                self = self, collision = collision
            };
            _beforeDoCollisionEnter2D?.Invoke(@params);
            self = @params.self;
            collision = @params.collision;
            orig(self, collision);
            if (_afterDoCollisionEnter2D != null) _afterDoCollisionEnter2D.Invoke(@params);
        }

        internal static void HookCheckTouching()
        {
            if (!HookedList.Contains("CheckTouching"))
            {
                HookedList.Add("CheckTouching");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CheckCollisionSideEnter), "CheckTouching"),
                    CheckTouching);
            }
        }

        internal static event Delegates.CheckTouching_BeforeArgs _beforeCheckTouching;
        internal static event Delegates.CheckTouching_NormalArgs _afterCheckTouching;

        private static void CheckTouching(Action<CheckCollisionSideEnter, LayerMask> orig, CheckCollisionSideEnter self,
            LayerMask layer)
        {
            Delegates.Params_CheckTouching @params = new()
            {
                self = self, layer = layer
            };
            _beforeCheckTouching?.Invoke(@params);
            self = @params.self;
            layer = @params.layer;
            orig(self, layer);
            if (_afterCheckTouching != null) _afterCheckTouching.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void CheckTouching_BeforeArgs(Params_CheckTouching args);

        public delegate void CheckTouching_NormalArgs(Params_CheckTouching args);

        public delegate void DoCollisionEnter2D_BeforeArgs(Params_DoCollisionEnter2D args);

        public delegate void DoCollisionEnter2D_NormalArgs(Params_DoCollisionEnter2D args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnExit_BeforeArgs(Params_OnExit args);

        public delegate void OnExit_NormalArgs(Params_OnExit args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_NormalArgs(Params_OnUpdate args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public CheckCollisionSideEnter self;
        }

        public sealed class Params_OnEnter
        {
            public CheckCollisionSideEnter self;
        }

        public sealed class Params_OnExit
        {
            public CheckCollisionSideEnter self;
        }

        public sealed class Params_OnUpdate
        {
            public CheckCollisionSideEnter self;
        }

        public sealed class Params_DoCollisionEnter2D
        {
            public Collision2D collision;
            public CheckCollisionSideEnter self;
        }

        public sealed class Params_CheckTouching
        {
            public LayerMask layer;
            public CheckCollisionSideEnter self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnExit_BeforeArgs OnExit
        {
            add
            {
                HookHandler._beforeOnExit += value;
                HookHandler.HookOnExit();
            }
            remove => HookHandler._beforeOnExit -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._beforeOnUpdate -= value;
        }

        public static event Delegates.DoCollisionEnter2D_BeforeArgs DoCollisionEnter2D
        {
            add
            {
                HookHandler._beforeDoCollisionEnter2D += value;
                HookHandler.HookDoCollisionEnter2D();
            }
            remove => HookHandler._beforeDoCollisionEnter2D -= value;
        }

        public static event Delegates.CheckTouching_BeforeArgs CheckTouching
        {
            add
            {
                HookHandler._beforeCheckTouching += value;
                HookHandler.HookCheckTouching();
            }
            remove => HookHandler._beforeCheckTouching -= value;
        }
    }

