    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Awake
        {
            public CheckpointSprite self;
        }

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<CheckpointSprite> orig, CheckpointSprite self);

        public sealed class Params_OnEnable
        {
            public CheckpointSprite self;
        }

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_AfterArgs(Params_OnEnable args);

        public delegate void OnEnable_WithArgs(Action<CheckpointSprite> orig, CheckpointSprite self);

        public sealed class Params_Start
        {
            public CheckpointSprite self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<CheckpointSprite> orig, CheckpointSprite self);

        public sealed class Params_Show
        {
            public CheckpointSprite self;
        }

        public delegate void Show_BeforeArgs(Params_Show args);

        public delegate void Show_AfterArgs(Params_Show args);

        public delegate void Show_WithArgs(Action<CheckpointSprite> orig, CheckpointSprite self);

        public sealed class Params_Hide
        {
            public CheckpointSprite self;
        }

        public delegate void Hide_BeforeArgs(Params_Hide args);

        public delegate void Hide_AfterArgs(Params_Hide args);

        public delegate void Hide_WithArgs(Action<CheckpointSprite> orig, CheckpointSprite self);

        public sealed class Params_Update
        {
            public CheckpointSprite self;
        }

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_AfterArgs(Params_Update args);

        public delegate void Update_WithArgs(Action<CheckpointSprite> orig, CheckpointSprite self);

        public sealed class Params_Update_float
        {
            public CheckpointSprite self;
            public float deltaTime;
        }

        public delegate void Update_float_BeforeArgs(Params_Update_float args);

        public delegate void Update_float_AfterArgs(Params_Update_float args);

        public delegate void Update_float_WithArgs(Action<CheckpointSprite, float> orig, CheckpointSprite self,
            float deltaTime);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
