        private static List<string> HookedList = new List<string>();

        internal static void Hookget_VideoPlayer()
        {
            if (!HookedList.Contains("get_VideoPlayer"))
            {
                HookedList.Add("get_VideoPlayer");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(CinematicSequence), "get_VideoPlayer", true),
                    get_VideoPlayer);
            }
        }

        internal static event Delegates.get_VideoPlayer_BeforeArgs _beforeget_VideoPlayer;
        internal static event Delegates.get_VideoPlayer_AfterArgs _afterget_VideoPlayer;

        private static CinematicVideoPlayer get_VideoPlayer(Func<CinematicSequence, CinematicVideoPlayer> orig,
            CinematicSequence self)
        {
            Delegates.Params_get_VideoPlayer @params = new()
            {
                self = self
            };
            if (_beforeget_VideoPlayer != null)
            {
                foreach (Delegates.get_VideoPlayer_BeforeArgs toInvoke in _beforeget_VideoPlayer.GetInvocationList())
                {
                    try
                    {
                        _beforeget_VideoPlayer?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_VideoPlayer != null)
            {
                foreach (Delegates.get_VideoPlayer_AfterArgs toInvoke in _afterget_VideoPlayer.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_VideoPlayer.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

