    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_ContentObject_WithArgs get_ContentObject
        {
            add => HookEndpointManager.Add<Delegates.get_ContentObject_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ContentArea), "get_ContentObject"), value);
            remove => HookEndpointManager.Remove<Delegates.get_ContentObject_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ContentArea), "get_ContentObject"), value);
        }

        public static event Delegates.set_ContentObject_WithArgs set_ContentObject
        {
            add => HookEndpointManager.Add<Delegates.set_ContentObject_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(ContentArea), "set_ContentObject"), value);
