    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void Hookget_ContentObject()
        {
            if (!HookedList.Contains("get_ContentObject"))
            {
                HookedList.Add("get_ContentObject");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Modding.Menu.ContentArea), "get_ContentObject", true),
                    get_ContentObject);
            }
        }

        internal static event Delegates.get_ContentObject_BeforeArgs _beforeget_ContentObject;
        internal static event Delegates.get_ContentObject_AfterArgs _afterget_ContentObject;

        private static UnityEngine.GameObject get_ContentObject(
            Func<Modding.Menu.ContentArea, UnityEngine.GameObject> orig, Modding.Menu.ContentArea self)
        {
            Delegates.Params_get_ContentObject @params = new()
            {
                self = self
            };
            if (_beforeget_ContentObject != null)
            {
                foreach (Delegates.get_ContentObject_BeforeArgs toInvoke in
                         _beforeget_ContentObject.GetInvocationList())
                {
                    try
                    {
                        _beforeget_ContentObject?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_ContentObject != null)
            {
                foreach (Delegates.get_ContentObject_AfterArgs toInvoke in _afterget_ContentObject.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_ContentObject.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

