        internal static event Delegates.set_ContentObject_AfterArgs _afterset_ContentObject;

        private static void set_ContentObject(Action<Modding.Menu.ContentArea, UnityEngine.GameObject> orig,
            Modding.Menu.ContentArea self, UnityEngine.GameObject value)
        {
            Delegates.Params_set_ContentObject @params = new()
            {
                self = self, value = value
            };
            if (_beforeset_ContentObject != null)
            {
                foreach (Delegates.set_ContentObject_BeforeArgs toInvoke in
