namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for ConveyorMovement class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnConveyorMovement
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookOnEnable()
        {
            if (!HookedList.Contains("OnEnable"))
            {
                HookedList.Add("OnEnable");
                On.ConveyorMovement.OnEnable += OnEnable;
            }
        }

        internal static event Delegates.OnEnable_BeforeArgs _beforeOnEnable;
        internal static event Delegates.OnEnable_NormalArgs _afterOnEnable;

        private static void OnEnable(On.ConveyorMovement.orig_OnEnable orig, ConveyorMovement self)
        {
            Delegates.Params_OnEnable @params = new()
            {
                self = self
            };
            _beforeOnEnable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnable != null) _afterOnEnable.Invoke(@params);
        }

        internal static void HookStartConveyorMove()
        {
            if (!HookedList.Contains("StartConveyorMove"))
            {
                HookedList.Add("StartConveyorMove");
                On.ConveyorMovement.StartConveyorMove += StartConveyorMove;
            }
        }

        internal static event Delegates.StartConveyorMove_BeforeArgs _beforeStartConveyorMove;
        internal static event Delegates.StartConveyorMove_NormalArgs _afterStartConveyorMove;

        private static void StartConveyorMove(On.ConveyorMovement.orig_StartConveyorMove orig, ConveyorMovement self,
            float c_xSpeed, float c_ySpeed)
        {
            Delegates.Params_StartConveyorMove @params = new()
            {
                self = self, c_xSpeed = c_xSpeed, c_ySpeed = c_ySpeed
            };
            _beforeStartConveyorMove?.Invoke(@params);
            self = @params.self;
            c_xSpeed = @params.c_xSpeed;
            c_ySpeed = @params.c_ySpeed;
            orig(self, c_xSpeed, c_ySpeed);
            if (_afterStartConveyorMove != null) _afterStartConveyorMove.Invoke(@params);
        }

        internal static void HookStopConveyorMove()
        {
            if (!HookedList.Contains("StopConveyorMove"))
            {
                HookedList.Add("StopConveyorMove");
                On.ConveyorMovement.StopConveyorMove += StopConveyorMove;
            }
        }

        internal static event Delegates.StopConveyorMove_BeforeArgs _beforeStopConveyorMove;
        internal static event Delegates.StopConveyorMove_NormalArgs _afterStopConveyorMove;

        private static void StopConveyorMove(On.ConveyorMovement.orig_StopConveyorMove orig, ConveyorMovement self)
        {
            Delegates.Params_StopConveyorMove @params = new()
            {
                self = self
            };
            _beforeStopConveyorMove?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterStopConveyorMove != null) _afterStopConveyorMove.Invoke(@params);
        }

        internal static void HookLateUpdate()
        {
            if (!HookedList.Contains("LateUpdate"))
            {
                HookedList.Add("LateUpdate");
                On.ConveyorMovement.LateUpdate += LateUpdate;
            }
        }

        internal static event Delegates.LateUpdate_BeforeArgs _beforeLateUpdate;
        internal static event Delegates.LateUpdate_NormalArgs _afterLateUpdate;

        private static void LateUpdate(On.ConveyorMovement.orig_LateUpdate orig, ConveyorMovement self)
        {
            Delegates.Params_LateUpdate @params = new()
            {
                self = self
            };
            _beforeLateUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterLateUpdate != null) _afterLateUpdate.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void LateUpdate_BeforeArgs(Params_LateUpdate args);

        public delegate void LateUpdate_NormalArgs(Params_LateUpdate args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_NormalArgs(Params_OnEnable args);

        public delegate void StartConveyorMove_BeforeArgs(Params_StartConveyorMove args);

        public delegate void StartConveyorMove_NormalArgs(Params_StartConveyorMove args);

        public delegate void StopConveyorMove_BeforeArgs(Params_StopConveyorMove args);

        public delegate void StopConveyorMove_NormalArgs(Params_StopConveyorMove args);

        public sealed class Params_OnEnable
        {
            public ConveyorMovement self;
        }

        public sealed class Params_StartConveyorMove
        {
            public float c_xSpeed;
            public float c_ySpeed;
            public ConveyorMovement self;
        }

        public sealed class Params_StopConveyorMove
        {
            public ConveyorMovement self;
        }

        public sealed class Params_LateUpdate
        {
            public ConveyorMovement self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.StartConveyorMove_BeforeArgs StartConveyorMove
        {
            add
            {
                HookHandler._beforeStartConveyorMove += value;
                HookHandler.HookStartConveyorMove();
            }
            remove => HookHandler._beforeStartConveyorMove -= value;
        }

        public static event Delegates.StopConveyorMove_BeforeArgs StopConveyorMove
        {
            add
            {
                HookHandler._beforeStopConveyorMove += value;
                HookHandler.HookStopConveyorMove();
            }
            remove => HookHandler._beforeStopConveyorMove -= value;
        }

        public static event Delegates.LateUpdate_BeforeArgs LateUpdate
        {
            add
            {
                HookHandler._beforeLateUpdate += value;
                HookHandler.HookLateUpdate();
            }
            remove => HookHandler._beforeLateUpdate -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.OnEnable_NormalArgs OnEnable
        {
            add
            {
                HookHandler._afterOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._afterOnEnable -= value;
        }

        public static event Delegates.StartConveyorMove_NormalArgs StartConveyorMove
        {
            add
            {
                HookHandler._afterStartConveyorMove += value;
                HookHandler.HookStartConveyorMove();
            }
            remove => HookHandler._afterStartConveyorMove -= value;
        }

        public static event Delegates.StopConveyorMove_NormalArgs StopConveyorMove
        {
            add
            {
                HookHandler._afterStopConveyorMove += value;
                HookHandler.HookStopConveyorMove();
            }
            remove => HookHandler._afterStopConveyorMove -= value;
        }

        public static event Delegates.LateUpdate_NormalArgs LateUpdate
        {
            add
            {
                HookHandler._afterLateUpdate += value;
                HookHandler.HookLateUpdate();
            }
            remove => HookHandler._afterLateUpdate -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.ConveyorMovement.hook_OnEnable OnEnable
        {
            add => On.ConveyorMovement.OnEnable += value;
            remove => On.ConveyorMovement.OnEnable -= value;
        }

        public static event On.ConveyorMovement.hook_StartConveyorMove StartConveyorMove
        {
            add => On.ConveyorMovement.StartConveyorMove += value;
            remove => On.ConveyorMovement.StartConveyorMove -= value;
        }

        public static event On.ConveyorMovement.hook_StopConveyorMove StopConveyorMove
        {
            add => On.ConveyorMovement.StopConveyorMove += value;
            remove => On.ConveyorMovement.StopConveyorMove -= value;
        }

        public static event On.ConveyorMovement.hook_LateUpdate LateUpdate
        {
            add => On.ConveyorMovement.LateUpdate += value;
            remove => On.ConveyorMovement.LateUpdate -= value;
        }
    }
}