            {
                HookedList.Add("DoWalk");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(DistanceWalk), "DoWalk"), DoWalk);
            }
        }

        internal static event Delegates.DoWalk_BeforeArgs _beforeDoWalk;
        internal static event Delegates.DoWalk_NormalArgs _afterDoWalk;

        private static void DoWalk(Action<DistanceWalk> orig, DistanceWalk self)
