        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Awake
        {
            public EndBeta self;
        }

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<EndBeta> orig, EndBeta self);

        public sealed class Params_Start
        {
            public EndBeta self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<EndBeta> orig, EndBeta self);

        public sealed class Params_Update
        {
            public EndBeta self;
        }

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_AfterArgs(Params_Update args);

        public delegate void Update_WithArgs(Action<EndBeta> orig, EndBeta self);

        public sealed class Params_OnTriggerEnter2D
        {
            public EndBeta self;
            public UnityEngine.Collider2D otherCollider;
        }

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<EndBeta, UnityEngine.Collider2D> orig, EndBeta self,
            UnityEngine.Collider2D otherCollider);

        public sealed class Params_Reactivate
        {
            public EndBeta self;
        }

        public delegate void Reactivate_BeforeArgs(Params_Reactivate args);

        public delegate void Reactivate_AfterArgs(Params_Reactivate args);

        public delegate void Reactivate_WithArgs(Action<EndBeta> orig, EndBeta self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
        {
            add
            {
                HookHandler._beforeUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._beforeUpdate -= value;
        }

        public static event Delegates.OnTriggerEnter2D_BeforeArgs OnTriggerEnter2D
        {
            add
            {
                HookHandler._beforeOnTriggerEnter2D += value;
                HookHandler.HookOnTriggerEnter2D();
            }
            remove => HookHandler._beforeOnTriggerEnter2D -= value;
        }

        public static event Delegates.Reactivate_BeforeArgs Reactivate
        {
            add
            {
                HookHandler._beforeReactivate += value;
                HookHandler.HookReactivate();
            }
            remove => HookHandler._beforeReactivate -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
