namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for EnemyBullet class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnEnemyBullet
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookAwake()
        {
            if (!HookedList.Contains("Awake"))
            {
                HookedList.Add("Awake");
                On.EnemyBullet.Awake += Awake;
            }
        }

        internal static event Delegates.Awake_BeforeArgs _beforeAwake;
        internal static event Delegates.Awake_NormalArgs _afterAwake;

        private static void Awake(On.EnemyBullet.orig_Awake orig, EnemyBullet self)
        {
            Delegates.Params_Awake @params = new()
            {
                self = self
            };
            _beforeAwake?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterAwake != null) _afterAwake.Invoke(@params);
        }

        internal static void HookOnEnable()
        {
            if (!HookedList.Contains("OnEnable"))
            {
                HookedList.Add("OnEnable");
                On.EnemyBullet.OnEnable += OnEnable;
            }
        }

        internal static event Delegates.OnEnable_BeforeArgs _beforeOnEnable;
        internal static event Delegates.OnEnable_NormalArgs _afterOnEnable;

        private static void OnEnable(On.EnemyBullet.orig_OnEnable orig, EnemyBullet self)
        {
            Delegates.Params_OnEnable @params = new()
            {
                self = self
            };
            _beforeOnEnable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnable != null) _afterOnEnable.Invoke(@params);
        }

        internal static void HookUpdate()
        {
            if (!HookedList.Contains("Update"))
            {
                HookedList.Add("Update");
                On.EnemyBullet.Update += Update;
            }
        }

        internal static event Delegates.Update_BeforeArgs _beforeUpdate;
        internal static event Delegates.Update_NormalArgs _afterUpdate;

        private static void Update(On.EnemyBullet.orig_Update orig, EnemyBullet self)
        {
            Delegates.Params_Update @params = new()
            {
                self = self
            };
            _beforeUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterUpdate != null) _afterUpdate.Invoke(@params);
        }

        internal static void HookOnCollisionEnter2D()
        {
            if (!HookedList.Contains("OnCollisionEnter2D"))
            {
                HookedList.Add("OnCollisionEnter2D");
                On.EnemyBullet.OnCollisionEnter2D += OnCollisionEnter2D;
            }
        }

        internal static event Delegates.OnCollisionEnter2D_BeforeArgs _beforeOnCollisionEnter2D;
        internal static event Delegates.OnCollisionEnter2D_NormalArgs _afterOnCollisionEnter2D;

        private static void OnCollisionEnter2D(On.EnemyBullet.orig_OnCollisionEnter2D orig, EnemyBullet self,
            Collision2D collision)
        {
            Delegates.Params_OnCollisionEnter2D @params = new()
            {
                self = self, collision = collision
            };
            _beforeOnCollisionEnter2D?.Invoke(@params);
            self = @params.self;
            collision = @params.collision;
            orig(self, collision);
            if (_afterOnCollisionEnter2D != null) _afterOnCollisionEnter2D.Invoke(@params);
        }

        internal static void HookOnTriggerEnter2D()
        {
            if (!HookedList.Contains("OnTriggerEnter2D"))
            {
                HookedList.Add("OnTriggerEnter2D");
                On.EnemyBullet.OnTriggerEnter2D += OnTriggerEnter2D;
            }
        }

        internal static event Delegates.OnTriggerEnter2D_BeforeArgs _beforeOnTriggerEnter2D;
        internal static event Delegates.OnTriggerEnter2D_NormalArgs _afterOnTriggerEnter2D;

        private static void OnTriggerEnter2D(On.EnemyBullet.orig_OnTriggerEnter2D orig, EnemyBullet self,
            Collider2D collision)
        {
            Delegates.Params_OnTriggerEnter2D @params = new()
            {
                self = self, collision = collision
            };
            _beforeOnTriggerEnter2D?.Invoke(@params);
            self = @params.self;
            collision = @params.collision;
            orig(self, collision);
            if (_afterOnTriggerEnter2D != null) _afterOnTriggerEnter2D.Invoke(@params);
        }

        internal static void HookOrbitShieldHit()
        {
            if (!HookedList.Contains("OrbitShieldHit"))
            {
                HookedList.Add("OrbitShieldHit");
                On.EnemyBullet.OrbitShieldHit += OrbitShieldHit;
            }
        }

        internal static event Delegates.OrbitShieldHit_BeforeArgs _beforeOrbitShieldHit;
        internal static event Delegates.OrbitShieldHit_NormalArgs _afterOrbitShieldHit;

        private static void OrbitShieldHit(On.EnemyBullet.orig_OrbitShieldHit orig, EnemyBullet self, Transform shield)
        {
            Delegates.Params_OrbitShieldHit @params = new()
            {
                self = self, shield = shield
            };
            _beforeOrbitShieldHit?.Invoke(@params);
            self = @params.self;
            shield = @params.shield;
            orig(self, shield);
            if (_afterOrbitShieldHit != null) _afterOrbitShieldHit.Invoke(@params);
        }

        internal static void HookCollision()
        {
            if (!HookedList.Contains("Collision"))
            {
                HookedList.Add("Collision");
                On.EnemyBullet.Collision += Collision;
            }
        }

        internal static event Delegates.Collision_BeforeArgs _beforeCollision;

        private static IEnumerator Collision(On.EnemyBullet.orig_Collision orig, EnemyBullet self, Vector2 normal,
            bool doRotation)
        {
            Delegates.Params_Collision @params = new()
            {
                self = self, normal = normal, doRotation = doRotation
            };
            _beforeCollision?.Invoke(@params);
            self = @params.self;
            normal = @params.normal;
            doRotation = @params.doRotation;
            return orig(self, normal, doRotation);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_NormalArgs(Params_Awake args);

        public delegate void Collision_BeforeArgs(Params_Collision args);

        public delegate IEnumerator Collision_NormalArgs(Params_Collision args);

        public delegate void OnCollisionEnter2D_BeforeArgs(Params_OnCollisionEnter2D args);

        public delegate void OnCollisionEnter2D_NormalArgs(Params_OnCollisionEnter2D args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_NormalArgs(Params_OnEnable args);

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_NormalArgs(Params_OnTriggerEnter2D args);

        public delegate void OrbitShieldHit_BeforeArgs(Params_OrbitShieldHit args);

        public delegate void OrbitShieldHit_NormalArgs(Params_OrbitShieldHit args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_NormalArgs(Params_Update args);

        public sealed class Params_Awake
        {
            public EnemyBullet self;
        }

        public sealed class Params_OnEnable
        {
            public EnemyBullet self;
        }

        public sealed class Params_Update
        {
            public EnemyBullet self;
        }

        public sealed class Params_OnCollisionEnter2D
        {
            public Collision2D collision;
            public EnemyBullet self;
        }

        public sealed class Params_OnTriggerEnter2D
        {
            public Collider2D collision;
            public EnemyBullet self;
        }

        public sealed class Params_OrbitShieldHit
        {
            public EnemyBullet self;
            public Transform shield;
        }

        public sealed class Params_Collision
        {
            public bool doRotation;
            public Vector2 normal;
            public EnemyBullet self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
        {
            add
            {
                HookHandler._beforeUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._beforeUpdate -= value;
        }

        public static event Delegates.OnCollisionEnter2D_BeforeArgs OnCollisionEnter2D
        {
            add
            {
                HookHandler._beforeOnCollisionEnter2D += value;
                HookHandler.HookOnCollisionEnter2D();
            }
            remove => HookHandler._beforeOnCollisionEnter2D -= value;
        }

        public static event Delegates.OnTriggerEnter2D_BeforeArgs OnTriggerEnter2D
        {
            add
            {
                HookHandler._beforeOnTriggerEnter2D += value;
                HookHandler.HookOnTriggerEnter2D();
            }
            remove => HookHandler._beforeOnTriggerEnter2D -= value;
        }

        public static event Delegates.OrbitShieldHit_BeforeArgs OrbitShieldHit
        {
            add
            {
                HookHandler._beforeOrbitShieldHit += value;
                HookHandler.HookOrbitShieldHit();
            }
            remove => HookHandler._beforeOrbitShieldHit -= value;
        }

        public static event Delegates.Collision_BeforeArgs Collision
        {
            add
            {
                HookHandler._beforeCollision += value;
                HookHandler.HookCollision();
            }
            remove => HookHandler._beforeCollision -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
