    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.EnemyBullet.hook_Awake Awake
        {
            add => On.EnemyBullet.Awake += value;
            remove => On.EnemyBullet.Awake -= value;
        }

        public static event On.EnemyBullet.hook_OnEnable OnEnable
        {
            add => On.EnemyBullet.OnEnable += value;
            remove => On.EnemyBullet.OnEnable -= value;
        }

        public static event On.EnemyBullet.hook_Update Update
        {
            add => On.EnemyBullet.Update += value;
            remove => On.EnemyBullet.Update -= value;
        }

        public static event On.EnemyBullet.hook_OnCollisionEnter2D OnCollisionEnter2D
        {
            add => On.EnemyBullet.OnCollisionEnter2D += value;
            remove => On.EnemyBullet.OnCollisionEnter2D -= value;
        }

        public static event On.EnemyBullet.hook_OnTriggerEnter2D OnTriggerEnter2D
        {
            add => On.EnemyBullet.OnTriggerEnter2D += value;
            remove => On.EnemyBullet.OnTriggerEnter2D -= value;
        }

        public static event On.EnemyBullet.hook_OrbitShieldHit OrbitShieldHit
        {
            add => On.EnemyBullet.OrbitShieldHit += value;
            remove => On.EnemyBullet.OrbitShieldHit -= value;
        }

        public static event On.EnemyBullet.hook_Collision Collision
        {
            add => On.EnemyBullet.Collision += value;
            remove => On.EnemyBullet.Collision -= value;
        }
    }
}