using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for Explosion class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnExplosion
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.Explosion), "Reset"),
                    Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<HutongGames.PlayMaker.Actions.Explosion> orig,
            HutongGames.PlayMaker.Actions.Explosion self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnPreprocess()
        {
            if (!HookedList.Contains("OnPreprocess"))
            {
                HookedList.Add("OnPreprocess");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.Explosion), "OnPreprocess"),
                    OnPreprocess);
            }
        }

        internal static event Delegates.OnPreprocess_BeforeArgs _beforeOnPreprocess;
        internal static event Delegates.OnPreprocess_NormalArgs _afterOnPreprocess;

        private static void OnPreprocess(Action<HutongGames.PlayMaker.Actions.Explosion> orig,
            HutongGames.PlayMaker.Actions.Explosion self)
        {
            Delegates.Params_OnPreprocess @params = new()
            {
                self = self
            };
            _beforeOnPreprocess?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnPreprocess != null) _afterOnPreprocess.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.Explosion), "OnEnter"),
                    OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<HutongGames.PlayMaker.Actions.Explosion> orig,
            HutongGames.PlayMaker.Actions.Explosion self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookOnFixedUpdate()
        {
            if (!HookedList.Contains("OnFixedUpdate"))
            {
                HookedList.Add("OnFixedUpdate");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.Explosion), "OnFixedUpdate"),
                    OnFixedUpdate);
            }
        }

        internal static event Delegates.OnFixedUpdate_BeforeArgs _beforeOnFixedUpdate;
        internal static event Delegates.OnFixedUpdate_NormalArgs _afterOnFixedUpdate;

        private static void OnFixedUpdate(Action<HutongGames.PlayMaker.Actions.Explosion> orig,
            HutongGames.PlayMaker.Actions.Explosion self)
        {
            Delegates.Params_OnFixedUpdate @params = new()
            {
                self = self
            };
            _beforeOnFixedUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnFixedUpdate != null) _afterOnFixedUpdate.Invoke(@params);
        }

        internal static void HookDoExplosion()
        {
            if (!HookedList.Contains("DoExplosion"))
            {
                HookedList.Add("DoExplosion");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.Explosion), "DoExplosion"),
                    DoExplosion);
            }
        }

        internal static event Delegates.DoExplosion_BeforeArgs _beforeDoExplosion;
        internal static event Delegates.DoExplosion_NormalArgs _afterDoExplosion;

        private static void DoExplosion(Action<HutongGames.PlayMaker.Actions.Explosion> orig,
            HutongGames.PlayMaker.Actions.Explosion self)
        {
            Delegates.Params_DoExplosion @params = new()
            {
                self = self
            };
            _beforeDoExplosion?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoExplosion != null) _afterDoExplosion.Invoke(@params);
        }

        internal static void HookShouldApplyForce()
        {
            if (!HookedList.Contains("ShouldApplyForce"))
            {
                HookedList.Add("ShouldApplyForce");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.Explosion), "ShouldApplyForce"),
                    ShouldApplyForce);
            }
        }

        internal static event Delegates.ShouldApplyForce_BeforeArgs _beforeShouldApplyForce;
        internal static event Delegates.ShouldApplyForce_NormalArgs _afterShouldApplyForce;

        private static bool ShouldApplyForce(Func<HutongGames.PlayMaker.Actions.Explosion, GameObject, bool> orig,
            HutongGames.PlayMaker.Actions.Explosion self, GameObject go)
        {
            Delegates.Params_ShouldApplyForce @params = new()
            {
                self = self, go = go
            };
            _beforeShouldApplyForce?.Invoke(@params);
            self = @params.self;
            go = @params.go;
            var retVal = orig(self, go);
            if (_afterShouldApplyForce != null) retVal = _afterShouldApplyForce.Invoke(@params);
            return retVal;
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoExplosion_BeforeArgs(Params_DoExplosion args);

        public delegate void DoExplosion_NormalArgs(Params_DoExplosion args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnFixedUpdate_BeforeArgs(Params_OnFixedUpdate args);

        public delegate void OnFixedUpdate_NormalArgs(Params_OnFixedUpdate args);

        public delegate void OnPreprocess_BeforeArgs(Params_OnPreprocess args);

        public delegate void OnPreprocess_NormalArgs(Params_OnPreprocess args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public delegate void ShouldApplyForce_BeforeArgs(Params_ShouldApplyForce args);

        public delegate bool ShouldApplyForce_NormalArgs(Params_ShouldApplyForce args);

        public sealed class Params_Reset
        {
            public HutongGames.PlayMaker.Actions.Explosion self;
        }

        public sealed class Params_OnPreprocess
        {
            public HutongGames.PlayMaker.Actions.Explosion self;
        }

        public sealed class Params_OnEnter
        {
            public HutongGames.PlayMaker.Actions.Explosion self;
        }

        public sealed class Params_OnFixedUpdate
        {
            public HutongGames.PlayMaker.Actions.Explosion self;
        }

        public sealed class Params_DoExplosion
        {
            public HutongGames.PlayMaker.Actions.Explosion self;
        }

        public sealed class Params_ShouldApplyForce
        {
            public GameObject go;
            public HutongGames.PlayMaker.Actions.Explosion self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnPreprocess_BeforeArgs OnPreprocess
        {
            add
            {
                HookHandler._beforeOnPreprocess += value;
                HookHandler.HookOnPreprocess();
            }
            remove => HookHandler._beforeOnPreprocess -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnFixedUpdate_BeforeArgs OnFixedUpdate
        {
            add
            {
                HookHandler._beforeOnFixedUpdate += value;
                HookHandler.HookOnFixedUpdate();
            }
            remove => HookHandler._beforeOnFixedUpdate -= value;
        }

        public static event Delegates.DoExplosion_BeforeArgs DoExplosion
        {
            add
            {
                HookHandler._beforeDoExplosion += value;
                HookHandler.HookDoExplosion();
            }
            remove => HookHandler._beforeDoExplosion -= value;
        }

        public static event Delegates.ShouldApplyForce_BeforeArgs ShouldApplyForce
        {
            add
            {
                HookHandler._beforeShouldApplyForce += value;
                HookHandler.HookShouldApplyForce();
            }
            remove => HookHandler._beforeShouldApplyForce -= value;
        }
    }

    /// <summary>
