using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for FSMUtility_SetBoolFsmStateAction class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnFSMUtility_SetBoolFsmStateAction
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void Hookset_BoolValue()
        {
            if (!HookedList.Contains("set_BoolValue"))
            {
                HookedList.Add("set_BoolValue");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "set_BoolValue"),
                    set_BoolValue);
            }
        }

        internal static event Delegates.set_BoolValue_BeforeArgs _beforeset_BoolValue;
        internal static event Delegates.set_BoolValue_AfterArgs _afterset_BoolValue;

        private static void set_BoolValue(Action<FSMUtility.SetBoolFsmStateAction, bool> orig,
            FSMUtility.SetBoolFsmStateAction self, bool value)
        {
            Delegates.Params_set_BoolValue @params = new()
            {
                self = self, value = value
            };
            if (_beforeset_BoolValue != null)
                foreach (Delegates.set_BoolValue_BeforeArgs toInvoke in _beforeset_BoolValue.GetInvocationList())
                    try
                    {
                        _beforeset_BoolValue?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            value = @params.value;
            orig(self, value);
            if (_afterset_BoolValue != null)
                foreach (Delegates.set_BoolValue_AfterArgs toInvoke in _afterset_BoolValue.GetInvocationList())
                    try
                    {
                        _afterset_BoolValue.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<FSMUtility.SetBoolFsmStateAction> orig, FSMUtility.SetBoolFsmStateAction self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<FSMUtility.SetBoolFsmStateAction> orig,
            FSMUtility.SetBoolFsmStateAction self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<FSMUtility.SetBoolFsmStateAction> orig,
            FSMUtility.SetBoolFsmStateAction self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<FSMUtility.SetBoolFsmStateAction> orig,
            FSMUtility.SetBoolFsmStateAction self);

        public delegate void set_BoolValue_AfterArgs(Params_set_BoolValue args);

        public delegate void set_BoolValue_BeforeArgs(Params_set_BoolValue args);

        public delegate void set_BoolValue_WithArgs(Action<FSMUtility.SetBoolFsmStateAction, bool> orig,
            FSMUtility.SetBoolFsmStateAction self, bool value);

        public sealed class Params_set_BoolValue
        {
            public FSMUtility.SetBoolFsmStateAction self;
            public bool value;
        }

        public sealed class Params_Reset
        {
            public FSMUtility.SetBoolFsmStateAction self;
        }

        public sealed class Params_OnEnter
        {
            public FSMUtility.SetBoolFsmStateAction self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.set_BoolValue_BeforeArgs set_BoolValue
        {
            add
            {
                HookHandler._beforeset_BoolValue += value;
                HookHandler.Hookset_BoolValue();
            }
            remove => HookHandler._beforeset_BoolValue -= value;
        }

        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.set_BoolValue_AfterArgs set_BoolValue
        {
            add
            {
                HookHandler._afterset_BoolValue += value;
                HookHandler.Hookset_BoolValue();
            }
            remove => HookHandler._afterset_BoolValue -= value;
        }

        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.set_BoolValue_WithArgs set_BoolValue
        {
            add => HookEndpointManager.Add<Delegates.set_BoolValue_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "set_BoolValue"), value);
            remove => HookEndpointManager.Remove<Delegates.set_BoolValue_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "set_BoolValue"), value);
        }

        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "Reset"), value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(FSMUtility.SetBoolFsmStateAction), "OnEnter"), value);
        }
    }
}