using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for FastAction class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnFastAction
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookAdd()
        {
            if (!HookedList.Contains("Add"))
            {
                HookedList.Add("Add");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Add", true), Add);
            }
        }

        internal static event Delegates.Add_BeforeArgs _beforeAdd;
        internal static event Delegates.Add_AfterArgs _afterAdd;

        private static void Add(Action<TMPro.FastAction, System.Action> orig, TMPro.FastAction self, System.Action rhs)
        {
            Delegates.Params_Add @params = new()
            {
                self = self, rhs = rhs
            };
            if (_beforeAdd != null)
            {
                foreach (Delegates.Add_BeforeArgs toInvoke in _beforeAdd.GetInvocationList())
                {
                    try
                    {
                        _beforeAdd?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            rhs = @params.rhs;
            orig(self, rhs);
            if (_afterAdd != null)
            {
                foreach (Delegates.Add_AfterArgs toInvoke in _afterAdd.GetInvocationList())
                {
                    try
                    {
                        _afterAdd.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookRemove()
        {
            if (!HookedList.Contains("Remove"))
            {
                HookedList.Add("Remove");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Remove", true), Remove);
            }
        }

        internal static event Delegates.Remove_BeforeArgs _beforeRemove;
        internal static event Delegates.Remove_AfterArgs _afterRemove;

        private static void Remove(Action<TMPro.FastAction, System.Action> orig, TMPro.FastAction self,
            System.Action rhs)
        {
            Delegates.Params_Remove @params = new()
            {
                self = self, rhs = rhs
            };
            if (_beforeRemove != null)
            {
                foreach (Delegates.Remove_BeforeArgs toInvoke in _beforeRemove.GetInvocationList())
                {
                    try
                    {
                        _beforeRemove?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            rhs = @params.rhs;
            orig(self, rhs);
            if (_afterRemove != null)
            {
                foreach (Delegates.Remove_AfterArgs toInvoke in _afterRemove.GetInvocationList())
                {
                    try
                    {
                        _afterRemove.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookCall()
        {
            if (!HookedList.Contains("Call"))
            {
                HookedList.Add("Call");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Call", true), Call);
            }
        }

        internal static event Delegates.Call_BeforeArgs _beforeCall;
        internal static event Delegates.Call_AfterArgs _afterCall;

        private static void Call(Action<TMPro.FastAction> orig, TMPro.FastAction self)
        {
            Delegates.Params_Call @params = new()
            {
                self = self
            };
            if (_beforeCall != null)
            {
                foreach (Delegates.Call_BeforeArgs toInvoke in _beforeCall.GetInvocationList())
                {
                    try
                    {
                        _beforeCall?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterCall != null)
            {
                foreach (Delegates.Call_AfterArgs toInvoke in _afterCall.GetInvocationList())
                {
                    try
                    {
                        _afterCall.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Add
        {
            public TMPro.FastAction self;
            public System.Action rhs;
        }

        public delegate void Add_BeforeArgs(Params_Add args);

        public delegate void Add_AfterArgs(Params_Add args);

        public delegate void Add_WithArgs(Action<TMPro.FastAction, System.Action> orig, TMPro.FastAction self,
            System.Action rhs);

        public sealed class Params_Remove
        {
            public TMPro.FastAction self;
            public System.Action rhs;
        }

        public delegate void Remove_BeforeArgs(Params_Remove args);

        public delegate void Remove_AfterArgs(Params_Remove args);

        public delegate void Remove_WithArgs(Action<TMPro.FastAction, System.Action> orig, TMPro.FastAction self,
            System.Action rhs);

        public sealed class Params_Call
        {
            public TMPro.FastAction self;
        }

        public delegate void Call_BeforeArgs(Params_Call args);

        public delegate void Call_AfterArgs(Params_Call args);

        public delegate void Call_WithArgs(Action<TMPro.FastAction> orig, TMPro.FastAction self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Add_BeforeArgs Add
        {
            add
            {
                HookHandler._beforeAdd += value;
                HookHandler.HookAdd();
            }
            remove => HookHandler._beforeAdd -= value;
        }

        public static event Delegates.Remove_BeforeArgs Remove
        {
            add
            {
                HookHandler._beforeRemove += value;
                HookHandler.HookRemove();
            }
            remove => HookHandler._beforeRemove -= value;
        }

        public static event Delegates.Call_BeforeArgs Call
        {
            add
            {
                HookHandler._beforeCall += value;
                HookHandler.HookCall();
            }
            remove => HookHandler._beforeCall -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Add_AfterArgs Add
        {
            add
            {
                HookHandler._afterAdd += value;
                HookHandler.HookAdd();
            }
            remove => HookHandler._afterAdd -= value;
        }

        public static event Delegates.Remove_AfterArgs Remove
        {
            add
            {
                HookHandler._afterRemove += value;
                HookHandler.HookRemove();
            }
            remove => HookHandler._afterRemove -= value;
        }

        public static event Delegates.Call_AfterArgs Call
        {
            add
            {
                HookHandler._afterCall += value;
                HookHandler.HookCall();
            }
            remove => HookHandler._afterCall -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Add_WithArgs Add
        {
            add => HookEndpointManager.Add<Delegates.Add_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Add", true), value);
            remove => HookEndpointManager.Remove<Delegates.Add_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Add", true), value);
        }

        public static event Delegates.Remove_WithArgs Remove
        {
            add => HookEndpointManager.Add<Delegates.Remove_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Remove", true), value);
            remove => HookEndpointManager.Remove<Delegates.Remove_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Remove", true), value);
        }

        public static event Delegates.Call_WithArgs Call
        {
            add => HookEndpointManager.Add<Delegates.Call_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Call", true), value);
            remove => HookEndpointManager.Remove<Delegates.Call_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(TMPro.FastAction), "Call", true), value);
        }
    }
}