                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookDoCheck()
        {
            if (!HookedList.Contains("DoCheck"))
            {
                HookedList.Add("DoCheck");
                On.GGWaitForBossSceneTransitionIn.DoCheck += DoCheck;
            }
        }

        internal static event Delegates.DoCheck_BeforeArgs _beforeDoCheck;
        internal static event Delegates.DoCheck_AfterArgs _afterDoCheck;

        private static void DoCheck(On.GGWaitForBossSceneTransitionIn.orig_DoCheck orig,
            GGWaitForBossSceneTransitionIn self)
        {
            Delegates.Params_DoCheck @params = new()
            {
                self = self
            };
            if (_beforeDoCheck != null)
                foreach (Delegates.DoCheck_BeforeArgs toInvoke in _beforeDoCheck.GetInvocationList())
                    try
                    {
                        _beforeDoCheck?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterDoCheck != null)
                foreach (Delegates.DoCheck_AfterArgs toInvoke in _afterDoCheck.GetInvocationList())
                    try
                    {
                        _afterDoCheck.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoCheck_AfterArgs(Params_DoCheck args);

        public delegate void DoCheck_BeforeArgs(Params_DoCheck args);

        public delegate void DoCheck_WithArgs(Action<GGWaitForBossSceneTransitionIn> orig,
            GGWaitForBossSceneTransitionIn self);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<GGWaitForBossSceneTransitionIn> orig,
            GGWaitForBossSceneTransitionIn self);

        public delegate void OnUpdate_AfterArgs(Params_OnUpdate args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_WithArgs(Action<GGWaitForBossSceneTransitionIn> orig,
            GGWaitForBossSceneTransitionIn self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<GGWaitForBossSceneTransitionIn> orig,
            GGWaitForBossSceneTransitionIn self);

        public sealed class Params_Reset
        {
            public GGWaitForBossSceneTransitionIn self;
        }

        public sealed class Params_OnEnter
        {
            public GGWaitForBossSceneTransitionIn self;
        }

        public sealed class Params_OnUpdate
        {
            public GGWaitForBossSceneTransitionIn self;
        }

        public sealed class Params_DoCheck
        {
            public GGWaitForBossSceneTransitionIn self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
