        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.GGWaitForBossSceneTransitionIn.hook_Reset Reset
        {
            add => On.GGWaitForBossSceneTransitionIn.Reset += value;
            remove => On.GGWaitForBossSceneTransitionIn.Reset -= value;
        }

        public static event On.GGWaitForBossSceneTransitionIn.hook_OnEnter OnEnter
        {
            add => On.GGWaitForBossSceneTransitionIn.OnEnter += value;
            remove => On.GGWaitForBossSceneTransitionIn.OnEnter -= value;
        }

        public static event On.GGWaitForBossSceneTransitionIn.hook_OnUpdate OnUpdate
        {
            add => On.GGWaitForBossSceneTransitionIn.OnUpdate += value;
            remove => On.GGWaitForBossSceneTransitionIn.OnUpdate -= value;
        }

        public static event On.GGWaitForBossSceneTransitionIn.hook_DoCheck DoCheck
        {
            add => On.GGWaitForBossSceneTransitionIn.DoCheck += value;
            remove => On.GGWaitForBossSceneTransitionIn.DoCheck -= value;
        }
    }
}