using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for GetAnimatorIsParameterControlledByCurve class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnGetAnimatorIsParameterControlledByCurve
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve), "Reset"),
                    Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<GetAnimatorIsParameterControlledByCurve> orig,
            GetAnimatorIsParameterControlledByCurve self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve), "OnEnter"),
                    OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<GetAnimatorIsParameterControlledByCurve> orig,
            GetAnimatorIsParameterControlledByCurve self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoCheckIsParameterControlledByCurve()
        {
            if (!HookedList.Contains("DoCheckIsParameterControlledByCurve"))
            {
                HookedList.Add("DoCheckIsParameterControlledByCurve");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve),
                        "DoCheckIsParameterControlledByCurve"), DoCheckIsParameterControlledByCurve);
            }
        }

        internal static event Delegates.DoCheckIsParameterControlledByCurve_BeforeArgs
            _beforeDoCheckIsParameterControlledByCurve;

        internal static event Delegates.DoCheckIsParameterControlledByCurve_NormalArgs
            _afterDoCheckIsParameterControlledByCurve;

        private static void DoCheckIsParameterControlledByCurve(Action<GetAnimatorIsParameterControlledByCurve> orig,
            GetAnimatorIsParameterControlledByCurve self)
        {
            Delegates.Params_DoCheckIsParameterControlledByCurve @params = new()
            {
                self = self
            };
            _beforeDoCheckIsParameterControlledByCurve?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoCheckIsParameterControlledByCurve != null)
                _afterDoCheckIsParameterControlledByCurve.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoCheckIsParameterControlledByCurve_BeforeArgs(
            Params_DoCheckIsParameterControlledByCurve args);

        public delegate void DoCheckIsParameterControlledByCurve_NormalArgs(
            Params_DoCheckIsParameterControlledByCurve args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public GetAnimatorIsParameterControlledByCurve self;
        }

        public sealed class Params_OnEnter
        {
            public GetAnimatorIsParameterControlledByCurve self;
        }

        public sealed class Params_DoCheckIsParameterControlledByCurve
        {
            public GetAnimatorIsParameterControlledByCurve self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoCheckIsParameterControlledByCurve_BeforeArgs DoCheckIsParameterControlledByCurve
        {
            add
            {
                HookHandler._beforeDoCheckIsParameterControlledByCurve += value;
                HookHandler.HookDoCheckIsParameterControlledByCurve();
            }
            remove => HookHandler._beforeDoCheckIsParameterControlledByCurve -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoCheckIsParameterControlledByCurve_NormalArgs DoCheckIsParameterControlledByCurve
        {
            add
            {
                HookHandler._afterDoCheckIsParameterControlledByCurve += value;
                HookHandler.HookDoCheckIsParameterControlledByCurve();
            }
            remove => HookHandler._afterDoCheckIsParameterControlledByCurve -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve), "OnEnter"), value);
        }

        public static event Delegates.DoCheckIsParameterControlledByCurve_NormalArgs DoCheckIsParameterControlledByCurve
        {
            add => HookEndpointManager.Add<Delegates.DoCheckIsParameterControlledByCurve_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve),
                    "DoCheckIsParameterControlledByCurve"), value);
            remove => HookEndpointManager.Remove<Delegates.DoCheckIsParameterControlledByCurve_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetAnimatorIsParameterControlledByCurve),
                    "DoCheckIsParameterControlledByCurve"), value);
        }
    }
}