using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for GetColliderRange class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnGetColliderRange
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetColliderRange), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<GetColliderRange> orig, GetColliderRange self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookGetRange()
        {
            if (!HookedList.Contains("GetRange"))
            {
                HookedList.Add("GetRange");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetColliderRange), "GetRange"), GetRange);
            }
        }

        internal static event Delegates.GetRange_BeforeArgs _beforeGetRange;
        internal static event Delegates.GetRange_AfterArgs _afterGetRange;

        private static void GetRange(Action<GetColliderRange> orig, GetColliderRange self)
        {
            Delegates.Params_GetRange @params = new()
            {
                self = self
            };
            if (_beforeGetRange != null)
                foreach (Delegates.GetRange_BeforeArgs toInvoke in _beforeGetRange.GetInvocationList())
                    try
                    {
                        _beforeGetRange?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterGetRange != null)
                foreach (Delegates.GetRange_AfterArgs toInvoke in _afterGetRange.GetInvocationList())
                    try
                    {
                        _afterGetRange.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetColliderRange), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<GetColliderRange> orig, GetColliderRange self)
        {
