                    {
                        _beforeDoGetFsmEnum?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterDoGetFsmEnum != null)
                foreach (Delegates.DoGetFsmEnum_AfterArgs toInvoke in _afterDoGetFsmEnum.GetInvocationList())
                    try
                    {
