            }
            remove => HookHandler._afterOnUpdate -= value;
        }

        public static event Delegates.DoGetFsmVariable_AfterArgs DoGetFsmVariable
        {
            add
            {
                HookHandler._afterDoGetFsmVariable += value;
                HookHandler.HookDoGetFsmVariable();
            }
            remove => HookHandler._afterDoGetFsmVariable -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "Reset"), value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "OnEnter"), value);
        }

        public static event Delegates.OnUpdate_WithArgs OnUpdate
        {
            add => HookEndpointManager.Add<Delegates.OnUpdate_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "OnUpdate"), value);
            remove => HookEndpointManager.Remove<Delegates.OnUpdate_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "OnUpdate"), value);
        }

        public static event Delegates.DoGetFsmVariable_WithArgs DoGetFsmVariable
        {
            add => HookEndpointManager.Add<Delegates.DoGetFsmVariable_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "DoGetFsmVariable"), value);
            remove => HookEndpointManager.Remove<Delegates.DoGetFsmVariable_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmRect), "DoGetFsmVariable"), value);
        }
    }
}