using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for GetFsmVariables class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnGetFsmVariables
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<GetFsmVariables> orig, GetFsmVariables self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookInitFsmVars()
        {
            if (!HookedList.Contains("InitFsmVars"))
            {
                HookedList.Add("InitFsmVars");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "InitFsmVars"), InitFsmVars);
            }
        }

        internal static event Delegates.InitFsmVars_BeforeArgs _beforeInitFsmVars;
        internal static event Delegates.InitFsmVars_NormalArgs _afterInitFsmVars;

        private static void InitFsmVars(Action<GetFsmVariables> orig, GetFsmVariables self)
        {
            Delegates.Params_InitFsmVars @params = new()
            {
                self = self
            };
            _beforeInitFsmVars?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterInitFsmVars != null) _afterInitFsmVars.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<GetFsmVariables> orig, GetFsmVariables self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookOnUpdate()
        {
            if (!HookedList.Contains("OnUpdate"))
            {
                HookedList.Add("OnUpdate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "OnUpdate"), OnUpdate);
            }
        }

        internal static event Delegates.OnUpdate_BeforeArgs _beforeOnUpdate;
        internal static event Delegates.OnUpdate_NormalArgs _afterOnUpdate;

        private static void OnUpdate(Action<GetFsmVariables> orig, GetFsmVariables self)
        {
            Delegates.Params_OnUpdate @params = new()
            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookDoGetFsmVariables()
        {
            if (!HookedList.Contains("DoGetFsmVariables"))
            {
                HookedList.Add("DoGetFsmVariables");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "DoGetFsmVariables"),
                    DoGetFsmVariables);
            }
        }

        internal static event Delegates.DoGetFsmVariables_BeforeArgs _beforeDoGetFsmVariables;
        internal static event Delegates.DoGetFsmVariables_NormalArgs _afterDoGetFsmVariables;

        private static void DoGetFsmVariables(Action<GetFsmVariables> orig, GetFsmVariables self)
        {
            Delegates.Params_DoGetFsmVariables @params = new()
            {
                self = self
            };
            _beforeDoGetFsmVariables?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoGetFsmVariables != null) _afterDoGetFsmVariables.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoGetFsmVariables_BeforeArgs(Params_DoGetFsmVariables args);

        public delegate void DoGetFsmVariables_NormalArgs(Params_DoGetFsmVariables args);

        public delegate void InitFsmVars_BeforeArgs(Params_InitFsmVars args);

        public delegate void InitFsmVars_NormalArgs(Params_InitFsmVars args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_NormalArgs(Params_OnUpdate args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public GetFsmVariables self;
        }

        public sealed class Params_InitFsmVars
        {
            public GetFsmVariables self;
        }

        public sealed class Params_OnEnter
        {
            public GetFsmVariables self;
        }

        public sealed class Params_OnUpdate
        {
            public GetFsmVariables self;
        }

        public sealed class Params_DoGetFsmVariables
        {
            public GetFsmVariables self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.InitFsmVars_BeforeArgs InitFsmVars
        {
            add
            {
                HookHandler._beforeInitFsmVars += value;
                HookHandler.HookInitFsmVars();
            }
            remove => HookHandler._beforeInitFsmVars -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._beforeOnUpdate -= value;
        }

        public static event Delegates.DoGetFsmVariables_BeforeArgs DoGetFsmVariables
        {
            add
            {
                HookHandler._beforeDoGetFsmVariables += value;
                HookHandler.HookDoGetFsmVariables();
            }
            remove => HookHandler._beforeDoGetFsmVariables -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.InitFsmVars_NormalArgs InitFsmVars
        {
            add
            {
                HookHandler._afterInitFsmVars += value;
                HookHandler.HookInitFsmVars();
            }
            remove => HookHandler._afterInitFsmVars -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add
            {
                HookHandler._afterOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._afterOnUpdate -= value;
        }

        public static event Delegates.DoGetFsmVariables_NormalArgs DoGetFsmVariables
        {
            add
            {
                HookHandler._afterDoGetFsmVariables += value;
                HookHandler.HookDoGetFsmVariables();
            }
            remove => HookHandler._afterDoGetFsmVariables -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "Reset"), value);
        }

        public static event Delegates.InitFsmVars_NormalArgs InitFsmVars
        {
            add => HookEndpointManager.Add<Delegates.InitFsmVars_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "InitFsmVars"), value);
            remove => HookEndpointManager.Remove<Delegates.InitFsmVars_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "InitFsmVars"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "OnEnter"), value);
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add => HookEndpointManager.Add<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "OnUpdate"), value);
            remove => HookEndpointManager.Remove<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "OnUpdate"), value);
        }

        public static event Delegates.DoGetFsmVariables_NormalArgs DoGetFsmVariables
        {
            add => HookEndpointManager.Add<Delegates.DoGetFsmVariables_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "DoGetFsmVariables"), value);
            remove => HookEndpointManager.Remove<Delegates.DoGetFsmVariables_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetFsmVariables), "DoGetFsmVariables"), value);
        }
    }
}