using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for GetLocationInfo class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnGetLocationInfo
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo), "Reset",
                        true), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<HutongGames.PlayMaker.Actions.GetLocationInfo> orig,
            HutongGames.PlayMaker.Actions.GetLocationInfo self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
            {
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                {
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
            {
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                {
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo), "OnEnter",
                        true), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<HutongGames.PlayMaker.Actions.GetLocationInfo> orig,
            HutongGames.PlayMaker.Actions.GetLocationInfo self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
            {
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                {
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
            {
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                {
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookDoGetLocationInfo()
        {
            if (!HookedList.Contains("DoGetLocationInfo"))
            {
                HookedList.Add("DoGetLocationInfo");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo),
                        "DoGetLocationInfo", true), DoGetLocationInfo);
            }
        }

        internal static event Delegates.DoGetLocationInfo_BeforeArgs _beforeDoGetLocationInfo;
        internal static event Delegates.DoGetLocationInfo_AfterArgs _afterDoGetLocationInfo;

        private static void DoGetLocationInfo(Action<HutongGames.PlayMaker.Actions.GetLocationInfo> orig,
            HutongGames.PlayMaker.Actions.GetLocationInfo self)
        {
            Delegates.Params_DoGetLocationInfo @params = new()
            {
                self = self
            };
            if (_beforeDoGetLocationInfo != null)
            {
                foreach (Delegates.DoGetLocationInfo_BeforeArgs toInvoke in
                         _beforeDoGetLocationInfo.GetInvocationList())
                {
                    try
                    {
                        _beforeDoGetLocationInfo?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterDoGetLocationInfo != null)
            {
                foreach (Delegates.DoGetLocationInfo_AfterArgs toInvoke in _afterDoGetLocationInfo.GetInvocationList())
                {
                    try
                    {
                        _afterDoGetLocationInfo.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Reset
        {
            public HutongGames.PlayMaker.Actions.GetLocationInfo self;
        }

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<HutongGames.PlayMaker.Actions.GetLocationInfo> orig,
            HutongGames.PlayMaker.Actions.GetLocationInfo self);

        public sealed class Params_OnEnter
        {
            public HutongGames.PlayMaker.Actions.GetLocationInfo self;
        }

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<HutongGames.PlayMaker.Actions.GetLocationInfo> orig,
            HutongGames.PlayMaker.Actions.GetLocationInfo self);

        public sealed class Params_DoGetLocationInfo
        {
            public HutongGames.PlayMaker.Actions.GetLocationInfo self;
        }

        public delegate void DoGetLocationInfo_BeforeArgs(Params_DoGetLocationInfo args);

        public delegate void DoGetLocationInfo_AfterArgs(Params_DoGetLocationInfo args);

        public delegate void DoGetLocationInfo_WithArgs(Action<HutongGames.PlayMaker.Actions.GetLocationInfo> orig,
            HutongGames.PlayMaker.Actions.GetLocationInfo self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoGetLocationInfo_BeforeArgs DoGetLocationInfo
        {
            add
            {
                HookHandler._beforeDoGetLocationInfo += value;
                HookHandler.HookDoGetLocationInfo();
            }
            remove => HookHandler._beforeDoGetLocationInfo -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoGetLocationInfo_AfterArgs DoGetLocationInfo
        {
            add
            {
                HookHandler._afterDoGetLocationInfo += value;
                HookHandler.HookDoGetLocationInfo();
            }
            remove => HookHandler._afterDoGetLocationInfo -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo), "Reset", true),
                value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo), "Reset", true),
                value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo), "OnEnter", true),
                value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo), "OnEnter", true),
                value);
        }

        public static event Delegates.DoGetLocationInfo_WithArgs DoGetLocationInfo
        {
            add => HookEndpointManager.Add<Delegates.DoGetLocationInfo_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo),
                    "DoGetLocationInfo", true), value);
            remove => HookEndpointManager.Remove<Delegates.DoGetLocationInfo_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.GetLocationInfo),
                    "DoGetLocationInfo", true), value);
        }
    }
}