using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for GetNextOverlapArea2d class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnGetNextOverlapArea2d
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<GetNextOverlapArea2d> orig, GetNextOverlapArea2d self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<GetNextOverlapArea2d> orig, GetNextOverlapArea2d self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoGetNextCollider()
        {
            if (!HookedList.Contains("DoGetNextCollider"))
            {
                HookedList.Add("DoGetNextCollider");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "DoGetNextCollider"),
                    DoGetNextCollider);
            }
        }

        internal static event Delegates.DoGetNextCollider_BeforeArgs _beforeDoGetNextCollider;
        internal static event Delegates.DoGetNextCollider_NormalArgs _afterDoGetNextCollider;

        private static void DoGetNextCollider(Action<GetNextOverlapArea2d> orig, GetNextOverlapArea2d self)
        {
            Delegates.Params_DoGetNextCollider @params = new()
            {
                self = self
            };
            _beforeDoGetNextCollider?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoGetNextCollider != null) _afterDoGetNextCollider.Invoke(@params);
        }

        internal static void HookGetOverlapAreaAll()
        {
            if (!HookedList.Contains("GetOverlapAreaAll"))
            {
                HookedList.Add("GetOverlapAreaAll");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "GetOverlapAreaAll"),
                    GetOverlapAreaAll);
            }
        }

        internal static event Delegates.GetOverlapAreaAll_BeforeArgs _beforeGetOverlapAreaAll;
        internal static event Delegates.GetOverlapAreaAll_NormalArgs _afterGetOverlapAreaAll;

        private static Collider2D[] GetOverlapAreaAll(Func<GetNextOverlapArea2d, Collider2D[]> orig,
            GetNextOverlapArea2d self)
        {
            Delegates.Params_GetOverlapAreaAll @params = new()
            {
                self = self
            };
            _beforeGetOverlapAreaAll?.Invoke(@params);
            self = @params.self;
            var retVal = orig(self);
            if (_afterGetOverlapAreaAll != null) retVal = _afterGetOverlapAreaAll.Invoke(@params);
            return retVal;
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoGetNextCollider_BeforeArgs(Params_DoGetNextCollider args);

        public delegate void DoGetNextCollider_NormalArgs(Params_DoGetNextCollider args);

        public delegate void GetOverlapAreaAll_BeforeArgs(Params_GetOverlapAreaAll args);

        public delegate Collider2D[] GetOverlapAreaAll_NormalArgs(Params_GetOverlapAreaAll args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public GetNextOverlapArea2d self;
        }

        public sealed class Params_OnEnter
        {
            public GetNextOverlapArea2d self;
        }

        public sealed class Params_DoGetNextCollider
        {
            public GetNextOverlapArea2d self;
        }

        public sealed class Params_GetOverlapAreaAll
        {
            public GetNextOverlapArea2d self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoGetNextCollider_BeforeArgs DoGetNextCollider
        {
            add
            {
                HookHandler._beforeDoGetNextCollider += value;
                HookHandler.HookDoGetNextCollider();
            }
            remove => HookHandler._beforeDoGetNextCollider -= value;
        }

        public static event Delegates.GetOverlapAreaAll_BeforeArgs GetOverlapAreaAll
        {
            add
            {
                HookHandler._beforeGetOverlapAreaAll += value;
                HookHandler.HookGetOverlapAreaAll();
            }
            remove => HookHandler._beforeGetOverlapAreaAll -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoGetNextCollider_NormalArgs DoGetNextCollider
        {
            add
            {
                HookHandler._afterDoGetNextCollider += value;
                HookHandler.HookDoGetNextCollider();
            }
            remove => HookHandler._afterDoGetNextCollider -= value;
        }

        public static event Delegates.GetOverlapAreaAll_NormalArgs GetOverlapAreaAll
        {
            add
            {
                HookHandler._afterGetOverlapAreaAll += value;
                HookHandler.HookGetOverlapAreaAll();
            }
            remove => HookHandler._afterGetOverlapAreaAll -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "OnEnter"), value);
        }

        public static event Delegates.DoGetNextCollider_NormalArgs DoGetNextCollider
        {
            add => HookEndpointManager.Add<Delegates.DoGetNextCollider_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "DoGetNextCollider"), value);
            remove => HookEndpointManager.Remove<Delegates.DoGetNextCollider_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "DoGetNextCollider"), value);
        }

        public static event Delegates.GetOverlapAreaAll_NormalArgs GetOverlapAreaAll
        {
            add => HookEndpointManager.Add<Delegates.GetOverlapAreaAll_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "GetOverlapAreaAll"), value);
            remove => HookEndpointManager.Remove<Delegates.GetOverlapAreaAll_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(GetNextOverlapArea2d), "GetOverlapAreaAll"), value);
        }
    }
}