                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookDoGetTag()
        {
            if (!HookedList.Contains("DoGetTag"))
            {
                HookedList.Add("DoGetTag");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(GetTag), "DoGetTag"),
                    DoGetTag);
            }
        }

        internal static event Delegates.DoGetTag_BeforeArgs _beforeDoGetTag;
        internal static event Delegates.DoGetTag_AfterArgs _afterDoGetTag;

        private static void DoGetTag(Action<GetTag> orig,
            GetTag self)
        {
            Delegates.Params_DoGetTag @params = new()
            {
                self = self
            };
            if (_beforeDoGetTag != null)
                foreach (Delegates.DoGetTag_BeforeArgs toInvoke in _beforeDoGetTag.GetInvocationList())
                    try
                    {
