                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterDoGetVector3XYZ != null)
                foreach (Delegates.DoGetVector3XYZ_AfterArgs toInvoke in _afterDoGetVector3XYZ.GetInvocationList())
