namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for HUDCamera class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnHUDCamera
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookOnEnable()
        {
            if (!HookedList.Contains("OnEnable"))
            {
                HookedList.Add("OnEnable");
                On.HUDCamera.OnEnable += OnEnable;
            }
        }

        internal static event Delegates.OnEnable_BeforeArgs _beforeOnEnable;
        internal static event Delegates.OnEnable_NormalArgs _afterOnEnable;

        private static void OnEnable(On.HUDCamera.orig_OnEnable orig, HUDCamera self)
        {
            Delegates.Params_OnEnable @params = new()
            {
                self = self
            };
            _beforeOnEnable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnable != null) _afterOnEnable.Invoke(@params);
        }

        internal static void HookMoveMenuToHUDCamera()
        {
            if (!HookedList.Contains("MoveMenuToHUDCamera"))
            {
                HookedList.Add("MoveMenuToHUDCamera");
                On.HUDCamera.MoveMenuToHUDCamera += MoveMenuToHUDCamera;
            }
        }

        internal static event Delegates.MoveMenuToHUDCamera_BeforeArgs _beforeMoveMenuToHUDCamera;
        internal static event Delegates.MoveMenuToHUDCamera_NormalArgs _afterMoveMenuToHUDCamera;

        private static void MoveMenuToHUDCamera(On.HUDCamera.orig_MoveMenuToHUDCamera orig, HUDCamera self)
        {
            Delegates.Params_MoveMenuToHUDCamera @params = new()
            {
                self = self
            };
            _beforeMoveMenuToHUDCamera?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterMoveMenuToHUDCamera != null) _afterMoveMenuToHUDCamera.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void MoveMenuToHUDCamera_BeforeArgs(Params_MoveMenuToHUDCamera args);

        public delegate void MoveMenuToHUDCamera_NormalArgs(Params_MoveMenuToHUDCamera args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_NormalArgs(Params_OnEnable args);

        public sealed class Params_OnEnable
        {
            public HUDCamera self;
        }

        public sealed class Params_MoveMenuToHUDCamera
        {
            public HUDCamera self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.MoveMenuToHUDCamera_BeforeArgs MoveMenuToHUDCamera
        {
            add
            {
                HookHandler._beforeMoveMenuToHUDCamera += value;
                HookHandler.HookMoveMenuToHUDCamera();
            }
            remove => HookHandler._beforeMoveMenuToHUDCamera -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.OnEnable_NormalArgs OnEnable
        {
            add
            {
                HookHandler._afterOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._afterOnEnable -= value;
        }

        public static event Delegates.MoveMenuToHUDCamera_NormalArgs MoveMenuToHUDCamera
        {
            add
            {
                HookHandler._afterMoveMenuToHUDCamera += value;
                HookHandler.HookMoveMenuToHUDCamera();
            }
            remove => HookHandler._afterMoveMenuToHUDCamera -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.HUDCamera.hook_OnEnable OnEnable
        {
            add => On.HUDCamera.OnEnable += value;
            remove => On.HUDCamera.OnEnable -= value;
        }

        public static event On.HUDCamera.hook_MoveMenuToHUDCamera MoveMenuToHUDCamera
        {
            add => On.HUDCamera.MoveMenuToHUDCamera += value;
            remove => On.HUDCamera.MoveMenuToHUDCamera -= value;
        }
    }
}