using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;
namespace HKMirror.Hooks.OnHooks;
/// <summary>
/// Contains different types of On Hooks for HashTableConcat class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnHashTableConcat
{
internal static class HookHandler
{
private static List<string> HookedList = new List<string>();
internal static void HookReset()
{
if (!HookedList.Contains("Reset"))
{
HookedList.Add("Reset");
new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.HashTableConcat), "Reset", true), Reset);

}
}
internal static event Delegates.Reset_BeforeArgs _beforeReset;
internal static event Delegates.Reset_AfterArgs _afterReset;
private static void Reset(Action<HutongGames.PlayMaker.Actions.HashTableConcat> orig, HutongGames.PlayMaker.Actions.HashTableConcat self)
{
Delegates.Params_Reset @params = new() 
{
self = self
};
if (_beforeReset != null)
{
foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
{
try
{
_beforeReset?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterReset != null)
{
foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
{
try
{
_afterReset.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookOnEnter()
{
if (!HookedList.Contains("OnEnter"))
{
HookedList.Add("OnEnter");
new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.HashTableConcat), "OnEnter", true), OnEnter);

}
}
internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;
private static void OnEnter(Action<HutongGames.PlayMaker.Actions.HashTableConcat> orig, HutongGames.PlayMaker.Actions.HashTableConcat self)
{
Delegates.Params_OnEnter @params = new() 
{
self = self
};
if (_beforeOnEnter != null)
{
foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
{
try
{
_beforeOnEnter?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterOnEnter != null)
{
foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
{
try
{
_afterOnEnter.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookDoHashTableConcat()
{
if (!HookedList.Contains("DoHashTableConcat"))
{
HookedList.Add("DoHashTableConcat");
new Hook(ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.HashTableConcat), "DoHashTableConcat", true), DoHashTableConcat);

}
}
internal static event Delegates.DoHashTableConcat_BeforeArgs _beforeDoHashTableConcat;
internal static event Delegates.DoHashTableConcat_AfterArgs _afterDoHashTableConcat;
private static void DoHashTableConcat(Action<HutongGames.PlayMaker.Actions.HashTableConcat,  System.Collections.Hashtable> orig, HutongGames.PlayMaker.Actions.HashTableConcat self,  System.Collections.Hashtable source)
{
Delegates.Params_DoHashTableConcat @params = new() 
{
self = self
, source = source
};
if (_beforeDoHashTableConcat != null)
{
foreach (Delegates.DoHashTableConcat_BeforeArgs toInvoke in _beforeDoHashTableConcat.GetInvocationList())
{
try
{
_beforeDoHashTableConcat?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
source = @params.source;
orig(self, source);
if (_afterDoHashTableConcat != null)
{
foreach (Delegates.DoHashTableConcat_AfterArgs toInvoke in _afterDoHashTableConcat.GetInvocationList())
{
try
{
_afterDoHashTableConcat.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
}

/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_Reset
{
public HutongGames.PlayMaker.Actions.HashTableConcat self;
}
public delegate void Reset_BeforeArgs(Params_Reset args);
public delegate void Reset_AfterArgs(Params_Reset args);
public delegate void Reset_WithArgs (Action<HutongGames.PlayMaker.Actions.HashTableConcat> orig, HutongGames.PlayMaker.Actions.HashTableConcat self);
public sealed class Params_OnEnter
{
public HutongGames.PlayMaker.Actions.HashTableConcat self;
}
public delegate void OnEnter_BeforeArgs(Params_OnEnter args);
public delegate void OnEnter_AfterArgs(Params_OnEnter args);
public delegate void OnEnter_WithArgs (Action<HutongGames.PlayMaker.Actions.HashTableConcat> orig, HutongGames.PlayMaker.Actions.HashTableConcat self);
public sealed class Params_DoHashTableConcat
{
public HutongGames.PlayMaker.Actions.HashTableConcat self;
public System.Collections.Hashtable source;
}
public delegate void DoHashTableConcat_BeforeArgs(Params_DoHashTableConcat args);
public delegate void DoHashTableConcat_AfterArgs(Params_DoHashTableConcat args);
public delegate void DoHashTableConcat_WithArgs (Action<HutongGames.PlayMaker.Actions.HashTableConcat,  System.Collections.Hashtable> orig, HutongGames.PlayMaker.Actions.HashTableConcat self,  System.Collections.Hashtable source);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.Reset_BeforeArgs Reset
{
add
{

HookHandler._beforeReset += value;
HookHandler.HookReset();
}
remove => HookHandler._beforeReset -= value;
}
public static event Delegates.OnEnter_BeforeArgs OnEnter
{
add
{

HookHandler._beforeOnEnter += value;
HookHandler.HookOnEnter();
}
remove => HookHandler._beforeOnEnter -= value;
}
public static event Delegates.DoHashTableConcat_BeforeArgs DoHashTableConcat
{
add
{

HookHandler._beforeDoHashTableConcat += value;
HookHandler.HookDoHashTableConcat();
}
remove => HookHandler._beforeDoHashTableConcat -= value;
}
}

